INSERT INTO `nguoidung_sanpham` (`MaSP`, `MaND`, `MaQuyen`, `MaDM`, `TenSP`, `MoTa`, `CachLam`, `HinhAnh1`, `HinhAnh2`, `HinhAnh3`, `TrangThai`, `ThoiGian`) VALUES
(1.23, 23, 1, 1, 'Cơm tấm', '<h4 class=\"ce-element ce-element--type-text\" id=\"1hchildmenuid1\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><span style=\"color: rgb(32, 33, 36); font-family: arial, sans-serif;\">Hương cơm tấm là&nbsp;</span><b style=\"color: rgb(32, 33, 36); font-family: arial, sans-serif;\">hương dịu nhẹ của mùi gạo pha lẫn mùi thơm của mỡ xào hành</b><span style=\"color: rgb(32, 33, 36); font-family: arial, sans-serif;\">. Cơm tấm thiếu mỡ hành nước mắm thì giống như một cô hoa hậu chân dài mà không có hồn văn hóa. Có lẽ cao nhân ẩm thực nói trên cũng là con nhà nghèo mới nghĩ ra cách ăn cơm mà không cần thức ăn yểm trợ như món cơm tấm này.</span><br></h4>', '<h4 class=\"ce-element ce-element--type-text\" id=\"1hchildmenuid1\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 1</steps>&nbsp;Nấu cơm</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Gạo tấm mua về vo sạch qua 2 - 3 lần và nấu trong nồi cơm điện như bình thường.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Nấu cơm cho món cơm tấm\" class=\"imgcontent lazy initial loaded\" data-id=\"2\" title=\"Nấu cơm cho món cơm tấm\" data-src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162224522338.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162224522338.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 753px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 753px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Nấu cơm trong nồi cơm điện</span></p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><quote class=\"quoteGreen\" style=\"margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 1px solid rgb(3, 120, 65); border-radius: 5px; background-color: rgb(241, 255, 249); line-height: 22px; text-indent: 20px; position: relative;\">Lưu ý: Để cơm tấm không bị nhão, bạn nên canh lượng nước khoảng một đốt ngón tay là được nhé.</quote></p><h4 class=\"ce-element ce-element--type-text\" id=\"1hchildmenuid2\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 2</steps>&nbsp;Làm phần sườn cốt lết nướng</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Thịt cốt lết mua về rửa sạch và dùng khăn thấm cho ráo. Tiếp đến, dùng búa đập thịt để làm thịt mỏng và mềm hơn (Nếu không có búa đập thịt bạn cũng có thể dùng chày giã bình thường).</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Ướp thịt theo công thức:&nbsp;<b style=\"margin: 0px; padding: 0px;\">5 cây sả</b>&nbsp;đập dập,&nbsp;<b style=\"margin: 0px; padding: 0px;\">½ muỗng bột ngọt</b>,&nbsp;<b style=\"margin: 0px; padding: 0px;\">1 muỗng hạt nêm</b>,&nbsp;<b style=\"margin: 0px; padding: 0px;\">1 muỗng mật ong</b>,&nbsp;<b style=\"margin: 0px; padding: 0px;\">2 muỗng dầu hào</b>,&nbsp;<b style=\"margin: 0px; padding: 0px;\">1 muỗng nước mắm</b>,&nbsp;<b style=\"margin: 0px; padding: 0px;\">¼ muỗng tiêu xay</b>,&nbsp;<b style=\"margin: 0px; padding: 0px;\">1 muỗng hành tím băm</b>,&nbsp;<b style=\"margin: 0px; padding: 0px;\">1 muỗng tỏi băm</b>&nbsp;và Nấu cơm cho món cơm tấm nhuyễn. Trộn đều và&nbsp;<b style=\"margin: 0px; padding: 0px;\">ướp thịt trong 1 tiếng</b>.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Cách ướp thịt để làm cơm tấm\" class=\"imgcontent lazy initial loaded\" data-id=\"3\" title=\"Cách ướp thịt để làm cơm tấm\" data-src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162227044516.png\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162227044516.png\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 753px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 753px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Đập thịt cho mỏng và ướp đều các loại gia vị</span></p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Nướng từng miếng thịt trên bếp than đến khi chín vàng đều hai mặt. Nếu bạn nướng bằng lò nướng hoặc nồi chiên không dầu có thể chỉnh nhiệt độ&nbsp;<b style=\"margin: 0px; padding: 0px;\">180 độ C từ 30 - 45 phút.</b></p><p class=\"ce-element ce-element--type-text ce-element--focused\" =\"\"=\"\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Nướng thịt cho món cơm tấm\" class=\"imgcontent lazy initial loaded\" data-id=\"4\" title=\"Nướng thịt cho món cơm tấm\" data-src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162227487950.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162227487950.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 753px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 753px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Nướng thịt đến khi vàng đều hai mặt</span></p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><quote class=\"quoteGreen\" style=\"margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 1px solid rgb(3, 120, 65); border-radius: 5px; background-color: rgb(241, 255, 249); line-height: 22px; text-indent: 20px; position: relative;\"><b style=\"margin: 0px; padding: 0px;\">Lưu ý:</b>&nbsp;Trong khi nướng, bạn nên quét thêm dầu lên hai mặt của thịt để thịt không bị khô.</quote></p><h4 id=\"1hchildmenuid3\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 3</steps>&nbsp;Làm chả trứng hấp</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Ngâm nấm mèo khô trong nước ấm 20 phút cho nở, sau đó rửa sạch, cắt bỏ phần chân và băm nhỏ. Miến ngâm trong 10 phút và cũng mang đi xắt nhỏ.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\">Trộn đều thịt nạc heo xay, mộc nhĩ và miến đã xắt nhỏ</b>. Đập thêm vào&nbsp;<b style=\"margin: 0px; padding: 0px;\">2 quả trứng gà và 1 lòng trắng</b>&nbsp;(1 lòng đỏ còn lại để tạo màu cho lớp bề mặt). Sau đó nêm với&nbsp;<b style=\"margin: 0px; padding: 0px;\">2 muỗng muối, 1 muỗng đường, 1 muỗng nước mắm</b>&nbsp;và&nbsp;<b style=\"margin: 0px; padding: 0px;\">1 muỗng hành tím</b>&nbsp;băm. Trộn đều để các nguyên liệu thấm đều gia vị.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Cho phần chả trứng vào<b style=\"margin: 0px; padding: 0px;\">&nbsp;hấp cách thủy trong 30 phút</b>. Sau đó&nbsp;<b style=\"margin: 0px; padding: 0px;\">cho lòng đỏ trứng còn lại lên bề mặt và hấp thêm 10 phút</b>&nbsp;là hoàn tất.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Chả trứng cho món cơm tấm\" class=\"imgcontent lazy initial loaded\" data-id=\"5\" title=\"Chả trứng cho món cơm tấm\" data-src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162229339436.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162229339436.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 753px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 753px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Chả trứng béo thơm và có lớp màu vàng đẹp mặt</span></p><p class=\"text-left ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><quote style=\"margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 2px solid rgb(195, 229, 248); border-radius: 5px; background-color: rgb(219, 237, 249); line-height: 22px;\">Tham khảo&nbsp;<a href=\"https://www.bachhoaxanh.com/kinh-nghiem-hay/cach-lam-cha-trung-pate-hap-moi-la-an-cung-com-tam-la-ngon-het-biet-1316007\" target=\"_blank\" style=\"margin: 0px; padding: 0px; color: rgb(0, 100, 194);\">cách làm chả trứng pate</a>&nbsp;nếu bạn muốn thử một hương vị mới nhé</quote></p><h4 class=\"ce-element ce-element--type-text\" id=\"1hchildmenuid4\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 4</steps>&nbsp;Làm bì heo</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bì heo mua về rửa sạch và mang đi&nbsp;<b style=\"margin: 0px; padding: 0px;\">luộc nhanh trong 15 phút</b>&nbsp;để bì giữ được độ dai.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Sau khi vớt ra cho ngay bì heo vào nước đá để giữ được độ giòn. Ngâm bì heo trong 5 phút và vớt ra để ráo. Xắt mỏng phần bì heo.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\">Làm thính</b>: Rang 60g gạo trắng đến khi chuyển sang màu vàng nâu. Để gạo cho nguội hoàn toàn và bỏ vào máy xay nhuyễn.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Đây là một trong những cách làm bì cơm tấm hay cách trộn bì cơm tấm ngon mà bạn không thể bỏ qua.</p><p class=\"ce-element ce-element--type-text ce-element--focused\" =\"\"=\"\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><quote class=\"quoteGreen\" style=\"margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 1px solid rgb(3, 120, 65); border-radius: 5px; background-color: rgb(241, 255, 249); line-height: 22px; text-indent: 20px; position: relative;\"><b style=\"margin: 0px; padding: 0px;\">Lưu ý:</b>&nbsp;Bạn cũng có thể mua&nbsp;<a href=\"https://www.bachhoaxanh.com/bot-gia-vi/bot-thinh-gao-thanh-loc-goi-150g\" target=\"_blank\" style=\"margin: 0px; padding: 0px; color: rgb(0, 100, 194);\">bột thính</a>&nbsp;làm sẵn để tiết kiệm thời gian.</quote></p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Làm bì heo cho món cơm tấm sườn bì chả\" class=\"imgcontent lazy initial loaded\" data-id=\"6\" title=\"Làm bì heo cho món cơm tấm sườn bì chả\" data-src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162238309900.png\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162238309900.png\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 753px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 753px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Trộn thính gạo vào bì heo</span></p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Trộn thính vào phần bì đã xắt nhuyễn là hoàn thành món bì heo.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Bì heo cho món cơm tấm sườn bì chả\" class=\"imgcontent lazy initial loaded\" data-id=\"7\" title=\"Bì heo cho món cơm tấm sườn bì chả\" data-src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162239178382.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162239178382.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 753px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 753px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Bì heo thơm phức và giòn dai lạ miệng</span></p><h4 class=\"ce-element ce-element--type-text\" id=\"1hchildmenuid5\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 5</steps>&nbsp;Làm đồ chua</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Làm đồ chua ăn cơm tấm\" class=\"imgcontent lazy initial loaded\" data-id=\"8\" title=\"Làm đồ chua ăn cơm tấm\" data-src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162240202988.png\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162240202988.png\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 753px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 753px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Xắt nhỏ cà rốt và củ cải trắng</span></p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Củ cải trắng và cà rốt mua về rửa sạch, gọt vỏ và bào thành sợi nhỏ.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Pha&nbsp;<b style=\"margin: 0px; padding: 0px;\">100ml giấm, 3 muỗng đường</b>&nbsp;và&nbsp;<b style=\"margin: 0px; padding: 0px;\">nửa muỗng muối</b>&nbsp;và đổ vào phần củ đã thái sợi. Trộn đều và&nbsp;<b style=\"margin: 0px; padding: 0px;\">ngâm trong 1 tiếng</b>&nbsp;là được.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Đồ chua sau khi ngâm 1 tiếng có thể gắp ra và dùng ngay</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Đồ chua để ăn cơm tấm\" class=\"imgcontent lazy initial loaded\" data-id=\"9\" title=\"Đồ chua để ăn cơm tấm\" data-src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162241079279.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162241079279.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 753px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 753px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Đồ chua để ăn cơm tấm</span></p><h4 class=\"ce-element ce-element--type-text\" id=\"1hchildmenuid6\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 6</steps>&nbsp;Làm phần mỡ hành</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Hành lá xắt nhuyễn cho thêm&nbsp;<b style=\"margin: 0px; padding: 0px;\">¼ muỗng muối, ¼ muỗng đường</b>&nbsp;và trộn đều.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Dầu ăn nấu sôi và chế vào phần hành lá đã chuẩn bị. Trộn cho phần hành lá chín hết là hoàn thành.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Làm mỡ hành ăn cơm tấm\" class=\"imgcontent lazy initial loaded\" data-id=\"10\" title=\"Làm mỡ hành ăn cơm tấm\" data-src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162241503640.png\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162241503640.png\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 753px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 753px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Làm mỡ hành ăn cơm tấm</span></p><h4 class=\"ce-element ce-element--type-text\" id=\"1hchildmenuid7\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 7</steps>&nbsp;Pha chế nước mắm</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Đun sôi và khuấy đều hỗn hợp&nbsp;<b style=\"margin: 0px; padding: 0px;\">3 muỗng nước mắm, 3 muỗng đường, 3 muỗng nước ấm</b>&nbsp;và&nbsp;<b style=\"margin: 0px; padding: 0px;\">1 muỗng giấm</b>. Để hỗn hợp nguội, sau đó&nbsp;<b style=\"margin: 0px; padding: 0px;\">thêm ½ muỗng mắm</b>&nbsp;và&nbsp;<b style=\"margin: 0px; padding: 0px;\">½ muỗng giấm</b>&nbsp;vào, nêm vừa miệng.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Ớt trái rửa sạch, đổ nước lạnh vừa đủ qua mặt ớt và đun sôi đến khi ớt mềm.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Dùng thìa dầm ớt nhỏ ra. Đun nóng&nbsp;<b style=\"margin: 0px; padding: 0px;\">1 muỗng dầu ăn</b>,&nbsp;<b style=\"margin: 0px; padding: 0px;\">cho 1 muỗng tỏi vào phi thơm</b>, sau đó&nbsp;<b style=\"margin: 0px; padding: 0px;\">cho phần ớt đã dầm nhuyễn vào</b>. Thêm vào&nbsp;<b style=\"margin: 0px; padding: 0px;\">1 muỗng đường, 1 muỗng giấm</b>, đun sôi khoảng 5 phút, tắt bếp để nguội rồi cho hỗn hợp ớt vào cùng phần nước mắm đã nấu.</p>', 'img/products/comtam1.jpg', '/img/products/comtam2.jpg', '/img/products/comtam3.jpg', 1, '2022-11-03 19:53:41');
INSERT INTO `nguoidung_sanpham` (`MaSP`, `MaND`, `MaQuyen`, `MaDM`, `TenSP`, `MoTa`, `CachLam`, `HinhAnh1`, `HinhAnh2`, `HinhAnh3`, `TrangThai`, `ThoiGian`) VALUES
(1.25, 25, 1, 1, 'Cơm tấm', '<h4 class=\"ce-element ce-element--type-text\" id=\"1hchildmenuid1\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><span style=\"color: rgb(32, 33, 36); font-family: arial, sans-serif;\">Hương cơm tấm là&nbsp;</span><b style=\"color: rgb(32, 33, 36); font-family: arial, sans-serif;\">hương dịu nhẹ của mùi gạo pha lẫn mùi thơm của mỡ xào hành</b><span style=\"color: rgb(32, 33, 36); font-family: arial, sans-serif;\">. Cơm tấm thiếu mỡ hành nước mắm thì giống như một cô hoa hậu chân dài mà không có hồn văn hóa. Có lẽ cao nhân ẩm thực nói trên cũng là con nhà nghèo mới nghĩ ra cách ăn cơm mà không cần thức ăn yểm trợ như món cơm tấm này.</span><br></h4>', '<h4 class=\"ce-element ce-element--type-text\" id=\"1hchildmenuid1\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 1</steps>&nbsp;Nấu cơm</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Gạo tấm mua về vo sạch qua 2 - 3 lần và nấu trong nồi cơm điện như bình thường.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Nấu cơm cho món cơm tấm\" class=\"imgcontent lazy initial loaded\" data-id=\"2\" title=\"Nấu cơm cho món cơm tấm\" data-src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162224522338.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162224522338.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 753px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 753px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Nấu cơm trong nồi cơm điện</span></p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><quote class=\"quoteGreen\" style=\"margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 1px solid rgb(3, 120, 65); border-radius: 5px; background-color: rgb(241, 255, 249); line-height: 22px; text-indent: 20px; position: relative;\">Lưu ý: Để cơm tấm không bị nhão, bạn nên canh lượng nước khoảng một đốt ngón tay là được nhé.</quote></p><h4 class=\"ce-element ce-element--type-text\" id=\"1hchildmenuid2\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 2</steps>&nbsp;Làm phần sườn cốt lết nướng</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Thịt cốt lết mua về rửa sạch và dùng khăn thấm cho ráo. Tiếp đến, dùng búa đập thịt để làm thịt mỏng và mềm hơn (Nếu không có búa đập thịt bạn cũng có thể dùng chày giã bình thường).</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Ướp thịt theo công thức:&nbsp;<b style=\"margin: 0px; padding: 0px;\">5 cây sả</b>&nbsp;đập dập,&nbsp;<b style=\"margin: 0px; padding: 0px;\">½ muỗng bột ngọt</b>,&nbsp;<b style=\"margin: 0px; padding: 0px;\">1 muỗng hạt nêm</b>,&nbsp;<b style=\"margin: 0px; padding: 0px;\">1 muỗng mật ong</b>,&nbsp;<b style=\"margin: 0px; padding: 0px;\">2 muỗng dầu hào</b>,&nbsp;<b style=\"margin: 0px; padding: 0px;\">1 muỗng nước mắm</b>,&nbsp;<b style=\"margin: 0px; padding: 0px;\">¼ muỗng tiêu xay</b>,&nbsp;<b style=\"margin: 0px; padding: 0px;\">1 muỗng hành tím băm</b>,&nbsp;<b style=\"margin: 0px; padding: 0px;\">1 muỗng tỏi băm</b>&nbsp;và Nấu cơm cho món cơm tấm nhuyễn. Trộn đều và&nbsp;<b style=\"margin: 0px; padding: 0px;\">ướp thịt trong 1 tiếng</b>.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Cách ướp thịt để làm cơm tấm\" class=\"imgcontent lazy initial loaded\" data-id=\"3\" title=\"Cách ướp thịt để làm cơm tấm\" data-src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162227044516.png\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162227044516.png\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 753px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 753px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Đập thịt cho mỏng và ướp đều các loại gia vị</span></p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Nướng từng miếng thịt trên bếp than đến khi chín vàng đều hai mặt. Nếu bạn nướng bằng lò nướng hoặc nồi chiên không dầu có thể chỉnh nhiệt độ&nbsp;<b style=\"margin: 0px; padding: 0px;\">180 độ C từ 30 - 45 phút.</b></p><p class=\"ce-element ce-element--type-text ce-element--focused\" =\"\"=\"\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Nướng thịt cho món cơm tấm\" class=\"imgcontent lazy initial loaded\" data-id=\"4\" title=\"Nướng thịt cho món cơm tấm\" data-src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162227487950.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162227487950.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 753px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 753px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Nướng thịt đến khi vàng đều hai mặt</span></p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><quote class=\"quoteGreen\" style=\"margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 1px solid rgb(3, 120, 65); border-radius: 5px; background-color: rgb(241, 255, 249); line-height: 22px; text-indent: 20px; position: relative;\"><b style=\"margin: 0px; padding: 0px;\">Lưu ý:</b>&nbsp;Trong khi nướng, bạn nên quét thêm dầu lên hai mặt của thịt để thịt không bị khô.</quote></p><h4 id=\"1hchildmenuid3\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 3</steps>&nbsp;Làm chả trứng hấp</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Ngâm nấm mèo khô trong nước ấm 20 phút cho nở, sau đó rửa sạch, cắt bỏ phần chân và băm nhỏ. Miến ngâm trong 10 phút và cũng mang đi xắt nhỏ.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\">Trộn đều thịt nạc heo xay, mộc nhĩ và miến đã xắt nhỏ</b>. Đập thêm vào&nbsp;<b style=\"margin: 0px; padding: 0px;\">2 quả trứng gà và 1 lòng trắng</b>&nbsp;(1 lòng đỏ còn lại để tạo màu cho lớp bề mặt). Sau đó nêm với&nbsp;<b style=\"margin: 0px; padding: 0px;\">2 muỗng muối, 1 muỗng đường, 1 muỗng nước mắm</b>&nbsp;và&nbsp;<b style=\"margin: 0px; padding: 0px;\">1 muỗng hành tím</b>&nbsp;băm. Trộn đều để các nguyên liệu thấm đều gia vị.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Cho phần chả trứng vào<b style=\"margin: 0px; padding: 0px;\">&nbsp;hấp cách thủy trong 30 phút</b>. Sau đó&nbsp;<b style=\"margin: 0px; padding: 0px;\">cho lòng đỏ trứng còn lại lên bề mặt và hấp thêm 10 phút</b>&nbsp;là hoàn tất.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Chả trứng cho món cơm tấm\" class=\"imgcontent lazy initial loaded\" data-id=\"5\" title=\"Chả trứng cho món cơm tấm\" data-src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162229339436.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162229339436.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 753px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 753px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Chả trứng béo thơm và có lớp màu vàng đẹp mặt</span></p><p class=\"text-left ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><quote style=\"margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 2px solid rgb(195, 229, 248); border-radius: 5px; background-color: rgb(219, 237, 249); line-height: 22px;\">Tham khảo&nbsp;<a href=\"https://www.bachhoaxanh.com/kinh-nghiem-hay/cach-lam-cha-trung-pate-hap-moi-la-an-cung-com-tam-la-ngon-het-biet-1316007\" target=\"_blank\" style=\"margin: 0px; padding: 0px; color: rgb(0, 100, 194);\">cách làm chả trứng pate</a>&nbsp;nếu bạn muốn thử một hương vị mới nhé</quote></p><h4 class=\"ce-element ce-element--type-text\" id=\"1hchildmenuid4\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 4</steps>&nbsp;Làm bì heo</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bì heo mua về rửa sạch và mang đi&nbsp;<b style=\"margin: 0px; padding: 0px;\">luộc nhanh trong 15 phút</b>&nbsp;để bì giữ được độ dai.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Sau khi vớt ra cho ngay bì heo vào nước đá để giữ được độ giòn. Ngâm bì heo trong 5 phút và vớt ra để ráo. Xắt mỏng phần bì heo.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\">Làm thính</b>: Rang 60g gạo trắng đến khi chuyển sang màu vàng nâu. Để gạo cho nguội hoàn toàn và bỏ vào máy xay nhuyễn.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Đây là một trong những cách làm bì cơm tấm hay cách trộn bì cơm tấm ngon mà bạn không thể bỏ qua.</p><p class=\"ce-element ce-element--type-text ce-element--focused\" =\"\"=\"\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><quote class=\"quoteGreen\" style=\"margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 1px solid rgb(3, 120, 65); border-radius: 5px; background-color: rgb(241, 255, 249); line-height: 22px; text-indent: 20px; position: relative;\"><b style=\"margin: 0px; padding: 0px;\">Lưu ý:</b>&nbsp;Bạn cũng có thể mua&nbsp;<a href=\"https://www.bachhoaxanh.com/bot-gia-vi/bot-thinh-gao-thanh-loc-goi-150g\" target=\"_blank\" style=\"margin: 0px; padding: 0px; color: rgb(0, 100, 194);\">bột thính</a>&nbsp;làm sẵn để tiết kiệm thời gian.</quote></p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Làm bì heo cho món cơm tấm sườn bì chả\" class=\"imgcontent lazy initial loaded\" data-id=\"6\" title=\"Làm bì heo cho món cơm tấm sườn bì chả\" data-src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162238309900.png\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162238309900.png\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 753px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 753px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Trộn thính gạo vào bì heo</span></p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Trộn thính vào phần bì đã xắt nhuyễn là hoàn thành món bì heo.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Bì heo cho món cơm tấm sườn bì chả\" class=\"imgcontent lazy initial loaded\" data-id=\"7\" title=\"Bì heo cho món cơm tấm sườn bì chả\" data-src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162239178382.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162239178382.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 753px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 753px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Bì heo thơm phức và giòn dai lạ miệng</span></p><h4 class=\"ce-element ce-element--type-text\" id=\"1hchildmenuid5\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 5</steps>&nbsp;Làm đồ chua</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Làm đồ chua ăn cơm tấm\" class=\"imgcontent lazy initial loaded\" data-id=\"8\" title=\"Làm đồ chua ăn cơm tấm\" data-src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162240202988.png\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162240202988.png\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 753px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 753px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Xắt nhỏ cà rốt và củ cải trắng</span></p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Củ cải trắng và cà rốt mua về rửa sạch, gọt vỏ và bào thành sợi nhỏ.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Pha&nbsp;<b style=\"margin: 0px; padding: 0px;\">100ml giấm, 3 muỗng đường</b>&nbsp;và&nbsp;<b style=\"margin: 0px; padding: 0px;\">nửa muỗng muối</b>&nbsp;và đổ vào phần củ đã thái sợi. Trộn đều và&nbsp;<b style=\"margin: 0px; padding: 0px;\">ngâm trong 1 tiếng</b>&nbsp;là được.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Đồ chua sau khi ngâm 1 tiếng có thể gắp ra và dùng ngay</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Đồ chua để ăn cơm tấm\" class=\"imgcontent lazy initial loaded\" data-id=\"9\" title=\"Đồ chua để ăn cơm tấm\" data-src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162241079279.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162241079279.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 753px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 753px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Đồ chua để ăn cơm tấm</span></p><h4 class=\"ce-element ce-element--type-text\" id=\"1hchildmenuid6\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 6</steps>&nbsp;Làm phần mỡ hành</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Hành lá xắt nhuyễn cho thêm&nbsp;<b style=\"margin: 0px; padding: 0px;\">¼ muỗng muối, ¼ muỗng đường</b>&nbsp;và trộn đều.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Dầu ăn nấu sôi và chế vào phần hành lá đã chuẩn bị. Trộn cho phần hành lá chín hết là hoàn thành.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Làm mỡ hành ăn cơm tấm\" class=\"imgcontent lazy initial loaded\" data-id=\"10\" title=\"Làm mỡ hành ăn cơm tấm\" data-src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162241503640.png\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/08/16/1375565/cach-nau-com-tam-suon-bi-cha-tai-nha-ngon-nhu-ngoai-tiem-202108162241503640.png\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 753px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 753px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Làm mỡ hành ăn cơm tấm</span></p><h4 class=\"ce-element ce-element--type-text\" id=\"1hchildmenuid7\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 7</steps>&nbsp;Pha chế nước mắm</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Đun sôi và khuấy đều hỗn hợp&nbsp;<b style=\"margin: 0px; padding: 0px;\">3 muỗng nước mắm, 3 muỗng đường, 3 muỗng nước ấm</b>&nbsp;và&nbsp;<b style=\"margin: 0px; padding: 0px;\">1 muỗng giấm</b>. Để hỗn hợp nguội, sau đó&nbsp;<b style=\"margin: 0px; padding: 0px;\">thêm ½ muỗng mắm</b>&nbsp;và&nbsp;<b style=\"margin: 0px; padding: 0px;\">½ muỗng giấm</b>&nbsp;vào, nêm vừa miệng.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Ớt trái rửa sạch, đổ nước lạnh vừa đủ qua mặt ớt và đun sôi đến khi ớt mềm.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Dùng thìa dầm ớt nhỏ ra. Đun nóng&nbsp;<b style=\"margin: 0px; padding: 0px;\">1 muỗng dầu ăn</b>,&nbsp;<b style=\"margin: 0px; padding: 0px;\">cho 1 muỗng tỏi vào phi thơm</b>, sau đó&nbsp;<b style=\"margin: 0px; padding: 0px;\">cho phần ớt đã dầm nhuyễn vào</b>. Thêm vào&nbsp;<b style=\"margin: 0px; padding: 0px;\">1 muỗng đường, 1 muỗng giấm</b>, đun sôi khoảng 5 phút, tắt bếp để nguội rồi cho hỗn hợp ớt vào cùng phần nước mắm đã nấu.</p>', 'img/products/comtam1.jpg', '/img/products/comtam2.jpg', '/img/products/comtam3.jpg', 1, '2022-11-03 19:53:41'),
(68.39, 39, 1, 1, 'Bánh mì thịt bò nướng', '<p><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">Ổ bánh mì giòn giòn khi phết lên một ít nước tương, đi kèm với thịt bò đậm vị, độ giòn từ dưa leo và đồ chua để cân bằng vị mặn, cuối cùng là rau ngò cùng với một ít tương ớt sẽ tạo nên món bánh mì thịt nướng thơm ngon, đậm đà hương vị.</span><br></p>', '<p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 1</steps>&nbsp;Sơ chế nguyên liệu</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Đầu tiên, bạn đem dưa chuột, tỏi, sả rửa sạch, vớt ra để ráo nước. Sau đó, bạn đem dưa chuột cắt làm tư theo chiều dọc, sả thì bạn cắt khoanh mỏng rồi và giã nhỏ.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Cắt chỉ củ cải, cà rốt, hành tím đâm dập\" class=\"imgcontent lazy initial loaded\" data-id=\"3\" title=\"Cắt chỉ củ cải, cà rốt, hành tím đâm dập\" data-src=\"https://cdn.tgdd.vn/Files/2021/07/27/1371175/huong-dan-3-cach-lam-banh-mi-bo-thom-ngon-de-lam-cho-bua-sang-du-chat-202107271622396990.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/07/27/1371175/huong-dan-3-cach-lam-banh-mi-bo-thom-ngon-de-lam-cho-bua-sang-du-chat-202107271622396990.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Tiếp đó, bạn đem cà rốt và củ cải gọt vỏ, rửa sạch để ráo và cắt chỉ cà rốt và củ cải. Hành tím lột vỏ, rửa sạch đem giã nhỏ còn tỏi bóc vỏ đem đi băm nhỏ., Rau ngò lặt bỏ lá già, lá úa và rễ, rửa sạch.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 2</steps>&nbsp;Ướp thịt bò</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bạn cho thịt bò băm vào một cái tô, sau đó cho gia vị vào theo tỉ lệ&nbsp;<b style=\"margin: 0px; padding: 0px;\">1/2 muỗng cà phê</b>&nbsp;ngũ vị hương,&nbsp;<b style=\"margin: 0px; padding: 0px;\">1/4 muỗng cà phê</b>&nbsp;muối,&nbsp;<b style=\"margin: 0px; padding: 0px;\">1/2 muỗng cà phê</b>&nbsp;tiêu xay,&nbsp;<b style=\"margin: 0px; padding: 0px;\">2 muỗng cà phê</b>&nbsp;nước mắm,&nbsp;<b style=\"margin: 0px; padding: 0px;\">2 muỗng cà phê</b>&nbsp;nước tương,&nbsp;<b style=\"margin: 0px; padding: 0px;\">1/2 muỗng cà phê</b>&nbsp;hắc xì dầu,&nbsp;<b style=\"margin: 0px; padding: 0px;\">2 muỗng cà phê</b>&nbsp;mật ong và&nbsp;<b style=\"margin: 0px; padding: 0px;\">2 muỗng cà phê</b>&nbsp;bột bắp cùng sả, tỏi và hành tím giã nhỏ để ướp thịt bò làm nhân.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Trộn thịt bò làm nhân chả\" class=\"imgcontent lazy initial loaded\" data-id=\"4\" title=\"Trộn thịt bò làm nhân chả\" data-src=\"https://cdn.tgdd.vn/Files/2021/07/27/1371175/huong-dan-3-cach-lam-banh-mi-bo-thom-ngon-de-lam-cho-bua-sang-du-chat-202107271622559660.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/07/27/1371175/huong-dan-3-cach-lam-banh-mi-bo-thom-ngon-de-lam-cho-bua-sang-du-chat-202107271622559660.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Chọ hết gia vị và thành phần cần thiết vào, bạn trộn điều lên để thịt ngấm gia vị trong 20 phút. Kế đó, bạn đem que tre đi ngâm nước .</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 3</steps>&nbsp;Làm đồ chua</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Làm đồ chua\" class=\"imgcontent lazy initial loaded\" data-id=\"5\" title=\"Làm đồ chua\" data-src=\"https://cdn.tgdd.vn/Files/2021/07/27/1371175/huong-dan-3-cach-lam-banh-mi-bo-thom-ngon-de-lam-cho-bua-sang-du-chat-202107271624115380.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/07/27/1371175/huong-dan-3-cach-lam-banh-mi-bo-thom-ngon-de-lam-cho-bua-sang-du-chat-202107271624115380.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Cà rốt và củ cải sau khi cắt chỉ trộn với ¼ muỗng cà phê muối. Sau đó, bạn lấy 1 cái chén cho vào 80ml giấm với 80ml nước và 3 muỗng canh đường, khuấy lên. Tiếp đó, bạn cho cà rốt và củ cải đã trộn qua muối vào&nbsp;<b style=\"margin: 0px; padding: 0px;\">ngâm 4 đến 5 tiếng.</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 4</steps>&nbsp;Làm sốt bánh mì</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bạn bắc 1 cái nồi lên bếp, cho 2 muỗng canh dầu ăn, cho tỏi băm vào phi thơm. Sau đó cho 2 muỗng canh tương đen, đảo cho đến khi sôi thì cho vào ½ chén nước và khuấy điều.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Làm sốt tương đen\" class=\"imgcontent lazy initial loaded\" data-id=\"6\" title=\"Làm sốt tương đen\" data-src=\"https://cdn.tgdd.vn/Files/2021/07/27/1371175/huong-dan-3-cach-lam-banh-mi-bo-thom-ngon-de-lam-cho-bua-sang-du-chat-202107271624265560.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/07/27/1371175/huong-dan-3-cach-lam-banh-mi-bo-thom-ngon-de-lam-cho-bua-sang-du-chat-202107271624265560.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Tiếp đó, bạnban cho vào&nbsp;<b style=\"margin: 0px; padding: 0px;\">2 muỗng cà phê</b>&nbsp;bột bắp với&nbsp;<b style=\"margin: 0px; padding: 0px;\">3 muỗng canh</b>&nbsp;nước, cho vào nồi sốt và khuấy đều tay cho nước sốt sệt lại là được.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 5</steps>&nbsp;Làm chả bò</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bạn lấy 1 lượng thịt bò vừa đủ và nắm nhẹ lại rồi dùng que tre xiên vào giữa để làm chả bò.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Nướng chả bò\" class=\"imgcontent lazy initial loaded\" data-id=\"7\" title=\"Nướng chả bò\" data-src=\"https://cdn.tgdd.vn/Files/2021/07/27/1371175/huong-dan-3-cach-lam-banh-mi-bo-thom-ngon-de-lam-cho-bua-sang-du-chat-202107271624436270.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/07/27/1371175/huong-dan-3-cach-lam-banh-mi-bo-thom-ngon-de-lam-cho-bua-sang-du-chat-202107271624436270.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Tiếp đó, bạn chuẩn bị một bếp nướng và quết 1 lớp dầu mỏng, đặt xiên thịt nướng 15 phút, bạn trở đều 2 mặt trong lúc nướng để thịt chín đều.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 6</steps>&nbsp;Hoàn thành</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Cho chả bỏ, dưa chuột, đồ chua, rau ngò vào bánh mì\" class=\"imgcontent lazy initial loaded\" data-id=\"8\" title=\"Cho chả bỏ, dưa chuột, đồ chua, rau ngò vào bánh mì\" data-src=\"https://cdn.tgdd.vn/Files/2021/07/27/1371175/huong-dan-3-cach-lam-banh-mi-bo-thom-ngon-de-lam-cho-bua-sang-du-chat-202107271625392220.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/07/27/1371175/huong-dan-3-cach-lam-banh-mi-bo-thom-ngon-de-lam-cho-bua-sang-du-chat-202107271625392220.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Khi chả bò nướng chín, bạn đặt một bên để nguội. Bạn lấy ổ bánh mì cắt đôi, cho dưa leo, cho chả bò vào, cho rau ngò và dưa chua, rưới nước sốt vào và thưởng thức</p>', 'img/products/banhmythitbo1.jpg', '/img/products/banhmythitbo2.jpg', '/img/products/banhmythitbo3.jpg', 1, '2022-10-23 18:14:01');
INSERT INTO `nguoidung_sanpham` (`MaSP`, `MaND`, `MaQuyen`, `MaDM`, `TenSP`, `MoTa`, `CachLam`, `HinhAnh1`, `HinhAnh2`, `HinhAnh3`, `TrangThai`, `ThoiGian`) VALUES
(69.43, 43, 1, 1, 'Bánh ướt', '<p><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">Hương thơm từ hành phi giòn tan khi ăn kèm với nước mắm ớt chua ngọt sẽ tạo nên một món bánh ướt ngon miệng và đơn giản.</span><br></p>', '<p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 1</steps>&nbsp;Xay gạo</b></p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Xay gạo\" class=\"imgcontent lazy initial loaded\" data-id=\"3\" title=\"Xay gạo\" data-src=\"https://cdn.tgdd.vn/Files/2022/03/18/1420975/3-cach-lam-banh-uot-bang-chao-chong-dinh-noi-hoi-xung-hap-202203180633213768.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2022/03/18/1420975/3-cach-lam-banh-uot-bang-chao-chong-dinh-noi-hoi-xung-hap-202203180633213768.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Gạo vo sạch ngâm trong 3 ngày, mỗi ngày thay nước 2 lần, cho gạo vào máy xay sinh tố và xay nhuyễn, lưu ý trong lúc xay nhớ cho nhiều nước (không nên cho quá nhiều để tránh hỗn hợp tràn ra ngoài). Sau khi xay xong cho hỗn hợp bột ra thau cùng 2 muỗng canh muối.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 2</steps>&nbsp;Lược bột</b></p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Lược bột\" class=\"imgcontent lazy initial loaded\" data-id=\"4\" title=\"Lược bột\" data-src=\"https://cdn.tgdd.vn/Files/2022/03/18/1420975/3-cach-lam-banh-uot-bang-chao-chong-dinh-noi-hoi-xung-hap-202203180633336476.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2022/03/18/1420975/3-cach-lam-banh-uot-bang-chao-chong-dinh-noi-hoi-xung-hap-202203180633336476.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Để bột lắng khoảng 1 tiếng thì tiến hành chắt bỏ phần nước trong bên trên, lưu ý nhớ chắt nhẹ tay để bột không bị chảy theo.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Dùng vải lược trải lên thau, cho hỗn hợp bột vào và tiến hành vắt nhẹ tay để nước chảy ra hết, sau khi hoàn thành ta thu được phần bột gạo tươi (bột gạo lọc).</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 3</steps>&nbsp;Pha bột bánh ướt</b></p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Pha bột bánh ướt\" class=\"imgcontent lazy initial loaded\" data-id=\"5\" title=\"Pha bột bánh ướt\" data-src=\"https://cdn.tgdd.vn/Files/2022/03/18/1420975/3-cach-lam-banh-uot-bang-chao-chong-dinh-noi-hoi-xung-hap-202203180633445358.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2022/03/18/1420975/3-cach-lam-banh-uot-bang-chao-chong-dinh-noi-hoi-xung-hap-202203180633445358.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Cho 120gr bột gạo lọc tươi vào ca đong, cho 200ml nước lạnh cùng ⅓ muỗng cà phê muối, thêm 150ml nước sôi vào, khuấy đều, cuối cùng cho 6 muỗng canh bột năng và 4 muỗng canh bột đậu xanh, trộn đều và để bột nghỉ trong 20 phút.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 4</steps>&nbsp;Làm bánh ướt</b></p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Làm bánh ướt\" class=\"imgcontent lazy initial loaded\" data-id=\"6\" title=\"Làm bánh ướt\" data-src=\"https://cdn.tgdd.vn/Files/2022/03/18/1420975/3-cach-lam-banh-uot-bang-chao-chong-dinh-noi-hoi-xung-hap-202203180633538159.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2022/03/18/1420975/3-cach-lam-banh-uot-bang-chao-chong-dinh-noi-hoi-xung-hap-202203180633538159.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Làm nóng chảo, cho 1 muỗng canh dầu ăn vào, tiếp đến múc một vá bột cho vào chảo và đậy nắp kín lại, khoảng 1 phút thì kiểm tra nếu bánh chín thì cho ra đĩa và quét một lớp dầu ăn lên trên bề mặt bánh, thực hiện lặp lại cho đến khi hết bột là được.</p>', 'img/products/banhuot1.jpg', '/img/products/banhuot2.jpg', '/img/products/banhuot3.jpg', 1, '2022-10-23 18:14:14'),
(71.26, 26, 1, 1, 'Bánh tằm bì', '<p><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"> Bánh tằm dai dai, bì heo bùi bùi sựt sựt, hương thơm từ rau sống và độ giòn từ dưa leo. Khi ăn thì chan nước cốt dừa lên rồi trộn đều sẽ tạo nên món ăn làm hài lòng thực khách.</span><br></p>', '<h4 class=\"ce-element ce-element--type-text\" id=\"2hchildmenuid1\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\">Bước 1: Nhào bột</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\" Nhào bột\" class=\"imgcontent\" data-id=\"6\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-thom-beo-dai-chuan-vi-mien-tay-202201061539009763.jpg\" title=\" Nhào bột\" width=\"360\" height=\"140\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Trộn đều bột gạo, bột nếp và bột năng lại với nhau. Sau đó, đổ từ từ nước sôi đã chuẩn bị vào bột, khuấy đều, rồi dùng tay nhồi tới khi được hỗn hợp đồng nhất, mịn và không dính tay.</p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Xong thì lấy một lượng bột vừa đủ để vào lòng bàn tay, rồi dùng hai tay se lại tạo thành những sợi vừa ăn.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><i style=\"margin: 0px; padding: 0px;\"><quote style=\"text-align: justify; margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 2px solid rgb(195, 229, 248); border-radius: 5px; background-color: rgb(219, 237, 249); line-height: 22px;\">Lưu ý, bạn nên phủ một ít bột áo để các sợi bánh không bị dính vào nhau nhé.</quote></i></p><h4 class=\"ce-element ce-element--type-text\" id=\"2hchildmenuid2\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\">Bước 2: Luộc bánh</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Luộc bánh\" class=\"imgcontent lazy initial loaded\" data-id=\"2\" title=\"Luộc bánh\" data-src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260921325522.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260921325522.jpg\" data-was-processed=\"true\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bắc nồi nước lên bếp, khi nước sôi thì thả các sợi bánh vào. Ta sẽ luộc đến khi thấy chúng nổi lên trên mặt và bột chuyển sang trong hơn thì vớt ra cho ngay vào tô nước đá nha.</p><h4 class=\"ce-element ce-element--type-text\" id=\"2hchildmenuid3\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\">Bước 3: Làm xốt</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Làm xốt\" class=\"imgcontent lazy initial loaded\" data-id=\"3\" title=\"Làm xốt\" data-src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260921514512.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260921514512.jpg\" data-was-processed=\"true\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Trước hết, cho khoảng&nbsp;<b style=\"margin: 0px; padding: 0px;\">30ml nước vào bột bắp</b>, khuấy đều đến khi tan hoàn toàn. Sau đó, bắc nồi lên bếp,&nbsp;<b style=\"margin: 0px; padding: 0px;\">cho hỗn hợp bột bắp cùng nước cốt dừa và ½ muỗng cà phê muối</b>&nbsp;vào. Cuối cùng, hãy nấu đến khi hỗn hợp sôi và sánh, sệt lại thì tắt bếp. Vậy là xong rồi đấy.</p><h4 class=\"ce-element ce-element--type-text\" id=\"2hchildmenuid4\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\">Bước 4: Làm bì</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Làm bì\" class=\"imgcontent lazy initial loaded\" data-id=\"4\" title=\"Làm bì\" data-src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260922143319.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260922143319.jpg\" data-was-processed=\"true\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Trước hết, lấy thịt nạc vai rửa sạch, rồi đem ướp cùng hành tím băm, 1 muỗng cà phê tỏi băm, ½ muỗng cà phê đường, 1 muỗng cà phê hạt nêm, 1 muỗng cà phê tiêu và 1 muỗng canh nước mắm. Trộn đều và để yên tầm 10 – 15 phút.</p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bắc chảo cùng dầu ăn lên bếp, khi dầu nóng thì cho thịt vào, chiên vàng đều. Sau đó, rót nước dừa vô, rim đến khi nước sền sệt thì tắt bếp. Xong thì cắt thịt và da heo luộc (đã chuẩn bị sẵn) thành sợi vừa ăn. Trộn đều cả hai cùng với thính gạo, vậy là có bì rồi nè.</p><h4 class=\"ce-element ce-element--type-text\" id=\"2hchildmenuid5\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\">Bước 5: Làm nước mắm</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\" Làm nước mắm\" class=\"imgcontent lazy initial loaded\" data-id=\"5\" title=\" Làm nước mắm\" data-src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260922323798.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260922323798.jpg\" data-was-processed=\"true\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Cho vào chén nước cốt chanh,&nbsp;<b style=\"margin: 0px; padding: 0px;\">70ml nước mắm, 60g đường cùng 80ml nước lọc</b>. Khuấy đều đến khi hòa tan hoàn toàn thì thêm&nbsp;<b style=\"margin: 0px; padding: 0px;\">½ muỗng cà phê tỏi băm và ớt băm</b>&nbsp;vào là được.</p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\">Tham khảo</b>:&nbsp;<a href=\"https://www.bachhoaxanh.com/kinh-nghiem-hay/cach-lam-banh-tam-khoai-mi-dan-da-deo-mem-thom-ngon-nuoc-cot-dua-1272402\" target=\"_blank\" style=\"margin: 0px; padding: 0px; color: rgb(0, 100, 194);\">Cách làm bánh tằm khoai mì dẻo mềm thơm ngon hấp dẫn</a></p><h3 class=\"ce-element ce-element--type-text\" id=\"hmenuid4\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-weight: bold; font-stretch: normal; font-size: 18px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><dropcaps style=\"margin: 0px; padding: 10px 10px 10px 0px; font-size: 56px; line-height: 36px; color: rgb(0, 136, 72); font-weight: normal; display: inline-block; vertical-align: middle;\">4</dropcaps>Thành phẩm</h3><p class=\"ce-element ce-element--type-text\" =\"\"=\"\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Bánh tằm bì \" class=\"imgcontent lazy initial loaded\" data-id=\"6\" title=\"Bánh tằm bì \" data-src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-thom-beo-dai-chuan-vi-mien-tay-202201051639478503.jpeg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-thom-beo-dai-chuan-vi-mien-tay-202201051639478503.jpeg\" data-was-processed=\"true\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p>', 'img/products/banhtambi1.jpg', '/img/products/banhtambi2.jpg', '/img/products/banhtambi3.jpg', 1, '2022-10-23 18:14:57'),
(71.32, 32, 1, 1, 'Bánh tằm bì', '<p><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"> Bánh tằm dai dai, bì heo bùi bùi sựt sựt, hương thơm từ rau sống và độ giòn từ dưa leo. Khi ăn thì chan nước cốt dừa lên rồi trộn đều sẽ tạo nên món ăn làm hài lòng thực khách.</span><br></p>', '<h4 class=\"ce-element ce-element--type-text\" id=\"2hchildmenuid1\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\">Bước 1: Nhào bột</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\" Nhào bột\" class=\"imgcontent\" data-id=\"6\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-thom-beo-dai-chuan-vi-mien-tay-202201061539009763.jpg\" title=\" Nhào bột\" width=\"360\" height=\"140\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Trộn đều bột gạo, bột nếp và bột năng lại với nhau. Sau đó, đổ từ từ nước sôi đã chuẩn bị vào bột, khuấy đều, rồi dùng tay nhồi tới khi được hỗn hợp đồng nhất, mịn và không dính tay.</p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Xong thì lấy một lượng bột vừa đủ để vào lòng bàn tay, rồi dùng hai tay se lại tạo thành những sợi vừa ăn.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><i style=\"margin: 0px; padding: 0px;\"><quote style=\"text-align: justify; margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 2px solid rgb(195, 229, 248); border-radius: 5px; background-color: rgb(219, 237, 249); line-height: 22px;\">Lưu ý, bạn nên phủ một ít bột áo để các sợi bánh không bị dính vào nhau nhé.</quote></i></p><h4 class=\"ce-element ce-element--type-text\" id=\"2hchildmenuid2\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\">Bước 2: Luộc bánh</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Luộc bánh\" class=\"imgcontent lazy initial loaded\" data-id=\"2\" title=\"Luộc bánh\" data-src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260921325522.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260921325522.jpg\" data-was-processed=\"true\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bắc nồi nước lên bếp, khi nước sôi thì thả các sợi bánh vào. Ta sẽ luộc đến khi thấy chúng nổi lên trên mặt và bột chuyển sang trong hơn thì vớt ra cho ngay vào tô nước đá nha.</p><h4 class=\"ce-element ce-element--type-text\" id=\"2hchildmenuid3\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\">Bước 3: Làm xốt</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Làm xốt\" class=\"imgcontent lazy initial loaded\" data-id=\"3\" title=\"Làm xốt\" data-src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260921514512.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260921514512.jpg\" data-was-processed=\"true\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Trước hết, cho khoảng&nbsp;<b style=\"margin: 0px; padding: 0px;\">30ml nước vào bột bắp</b>, khuấy đều đến khi tan hoàn toàn. Sau đó, bắc nồi lên bếp,&nbsp;<b style=\"margin: 0px; padding: 0px;\">cho hỗn hợp bột bắp cùng nước cốt dừa và ½ muỗng cà phê muối</b>&nbsp;vào. Cuối cùng, hãy nấu đến khi hỗn hợp sôi và sánh, sệt lại thì tắt bếp. Vậy là xong rồi đấy.</p><h4 class=\"ce-element ce-element--type-text\" id=\"2hchildmenuid4\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\">Bước 4: Làm bì</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Làm bì\" class=\"imgcontent lazy initial loaded\" data-id=\"4\" title=\"Làm bì\" data-src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260922143319.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260922143319.jpg\" data-was-processed=\"true\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Trước hết, lấy thịt nạc vai rửa sạch, rồi đem ướp cùng hành tím băm, 1 muỗng cà phê tỏi băm, ½ muỗng cà phê đường, 1 muỗng cà phê hạt nêm, 1 muỗng cà phê tiêu và 1 muỗng canh nước mắm. Trộn đều và để yên tầm 10 – 15 phút.</p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bắc chảo cùng dầu ăn lên bếp, khi dầu nóng thì cho thịt vào, chiên vàng đều. Sau đó, rót nước dừa vô, rim đến khi nước sền sệt thì tắt bếp. Xong thì cắt thịt và da heo luộc (đã chuẩn bị sẵn) thành sợi vừa ăn. Trộn đều cả hai cùng với thính gạo, vậy là có bì rồi nè.</p><h4 class=\"ce-element ce-element--type-text\" id=\"2hchildmenuid5\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\">Bước 5: Làm nước mắm</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\" Làm nước mắm\" class=\"imgcontent lazy initial loaded\" data-id=\"5\" title=\" Làm nước mắm\" data-src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260922323798.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260922323798.jpg\" data-was-processed=\"true\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Cho vào chén nước cốt chanh,&nbsp;<b style=\"margin: 0px; padding: 0px;\">70ml nước mắm, 60g đường cùng 80ml nước lọc</b>. Khuấy đều đến khi hòa tan hoàn toàn thì thêm&nbsp;<b style=\"margin: 0px; padding: 0px;\">½ muỗng cà phê tỏi băm và ớt băm</b>&nbsp;vào là được.</p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\">Tham khảo</b>:&nbsp;<a href=\"https://www.bachhoaxanh.com/kinh-nghiem-hay/cach-lam-banh-tam-khoai-mi-dan-da-deo-mem-thom-ngon-nuoc-cot-dua-1272402\" target=\"_blank\" style=\"margin: 0px; padding: 0px; color: rgb(0, 100, 194);\">Cách làm bánh tằm khoai mì dẻo mềm thơm ngon hấp dẫn</a></p><h3 class=\"ce-element ce-element--type-text\" id=\"hmenuid4\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-weight: bold; font-stretch: normal; font-size: 18px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><dropcaps style=\"margin: 0px; padding: 10px 10px 10px 0px; font-size: 56px; line-height: 36px; color: rgb(0, 136, 72); font-weight: normal; display: inline-block; vertical-align: middle;\">4</dropcaps>Thành phẩm</h3><p class=\"ce-element ce-element--type-text\" =\"\"=\"\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Bánh tằm bì \" class=\"imgcontent lazy initial loaded\" data-id=\"6\" title=\"Bánh tằm bì \" data-src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-thom-beo-dai-chuan-vi-mien-tay-202201051639478503.jpeg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-thom-beo-dai-chuan-vi-mien-tay-202201051639478503.jpeg\" data-was-processed=\"true\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p>', 'img/products/banhtambi1.jpg', '/img/products/banhtambi2.jpg', '/img/products/banhtambi3.jpg', 1, '2022-10-23 18:14:57');
INSERT INTO `nguoidung_sanpham` (`MaSP`, `MaND`, `MaQuyen`, `MaDM`, `TenSP`, `MoTa`, `CachLam`, `HinhAnh1`, `HinhAnh2`, `HinhAnh3`, `TrangThai`, `ThoiGian`) VALUES
(71.39, 39, 1, 1, 'Bánh tằm bì', '<p><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"> Bánh tằm dai dai, bì heo bùi bùi sựt sựt, hương thơm từ rau sống và độ giòn từ dưa leo. Khi ăn thì chan nước cốt dừa lên rồi trộn đều sẽ tạo nên món ăn làm hài lòng thực khách.</span><br></p>', '<h4 class=\"ce-element ce-element--type-text\" id=\"2hchildmenuid1\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\">Bước 1: Nhào bột</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\" Nhào bột\" class=\"imgcontent\" data-id=\"6\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-thom-beo-dai-chuan-vi-mien-tay-202201061539009763.jpg\" title=\" Nhào bột\" width=\"360\" height=\"140\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Trộn đều bột gạo, bột nếp và bột năng lại với nhau. Sau đó, đổ từ từ nước sôi đã chuẩn bị vào bột, khuấy đều, rồi dùng tay nhồi tới khi được hỗn hợp đồng nhất, mịn và không dính tay.</p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Xong thì lấy một lượng bột vừa đủ để vào lòng bàn tay, rồi dùng hai tay se lại tạo thành những sợi vừa ăn.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><i style=\"margin: 0px; padding: 0px;\"><quote style=\"text-align: justify; margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 2px solid rgb(195, 229, 248); border-radius: 5px; background-color: rgb(219, 237, 249); line-height: 22px;\">Lưu ý, bạn nên phủ một ít bột áo để các sợi bánh không bị dính vào nhau nhé.</quote></i></p><h4 class=\"ce-element ce-element--type-text\" id=\"2hchildmenuid2\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\">Bước 2: Luộc bánh</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Luộc bánh\" class=\"imgcontent lazy initial loaded\" data-id=\"2\" title=\"Luộc bánh\" data-src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260921325522.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260921325522.jpg\" data-was-processed=\"true\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bắc nồi nước lên bếp, khi nước sôi thì thả các sợi bánh vào. Ta sẽ luộc đến khi thấy chúng nổi lên trên mặt và bột chuyển sang trong hơn thì vớt ra cho ngay vào tô nước đá nha.</p><h4 class=\"ce-element ce-element--type-text\" id=\"2hchildmenuid3\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\">Bước 3: Làm xốt</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Làm xốt\" class=\"imgcontent lazy initial loaded\" data-id=\"3\" title=\"Làm xốt\" data-src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260921514512.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260921514512.jpg\" data-was-processed=\"true\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Trước hết, cho khoảng&nbsp;<b style=\"margin: 0px; padding: 0px;\">30ml nước vào bột bắp</b>, khuấy đều đến khi tan hoàn toàn. Sau đó, bắc nồi lên bếp,&nbsp;<b style=\"margin: 0px; padding: 0px;\">cho hỗn hợp bột bắp cùng nước cốt dừa và ½ muỗng cà phê muối</b>&nbsp;vào. Cuối cùng, hãy nấu đến khi hỗn hợp sôi và sánh, sệt lại thì tắt bếp. Vậy là xong rồi đấy.</p><h4 class=\"ce-element ce-element--type-text\" id=\"2hchildmenuid4\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\">Bước 4: Làm bì</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Làm bì\" class=\"imgcontent lazy initial loaded\" data-id=\"4\" title=\"Làm bì\" data-src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260922143319.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260922143319.jpg\" data-was-processed=\"true\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Trước hết, lấy thịt nạc vai rửa sạch, rồi đem ướp cùng hành tím băm, 1 muỗng cà phê tỏi băm, ½ muỗng cà phê đường, 1 muỗng cà phê hạt nêm, 1 muỗng cà phê tiêu và 1 muỗng canh nước mắm. Trộn đều và để yên tầm 10 – 15 phút.</p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bắc chảo cùng dầu ăn lên bếp, khi dầu nóng thì cho thịt vào, chiên vàng đều. Sau đó, rót nước dừa vô, rim đến khi nước sền sệt thì tắt bếp. Xong thì cắt thịt và da heo luộc (đã chuẩn bị sẵn) thành sợi vừa ăn. Trộn đều cả hai cùng với thính gạo, vậy là có bì rồi nè.</p><h4 class=\"ce-element ce-element--type-text\" id=\"2hchildmenuid5\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\">Bước 5: Làm nước mắm</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\" Làm nước mắm\" class=\"imgcontent lazy initial loaded\" data-id=\"5\" title=\" Làm nước mắm\" data-src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260922323798.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260922323798.jpg\" data-was-processed=\"true\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Cho vào chén nước cốt chanh,&nbsp;<b style=\"margin: 0px; padding: 0px;\">70ml nước mắm, 60g đường cùng 80ml nước lọc</b>. Khuấy đều đến khi hòa tan hoàn toàn thì thêm&nbsp;<b style=\"margin: 0px; padding: 0px;\">½ muỗng cà phê tỏi băm và ớt băm</b>&nbsp;vào là được.</p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\">Tham khảo</b>:&nbsp;<a href=\"https://www.bachhoaxanh.com/kinh-nghiem-hay/cach-lam-banh-tam-khoai-mi-dan-da-deo-mem-thom-ngon-nuoc-cot-dua-1272402\" target=\"_blank\" style=\"margin: 0px; padding: 0px; color: rgb(0, 100, 194);\">Cách làm bánh tằm khoai mì dẻo mềm thơm ngon hấp dẫn</a></p><h3 class=\"ce-element ce-element--type-text\" id=\"hmenuid4\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-weight: bold; font-stretch: normal; font-size: 18px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><dropcaps style=\"margin: 0px; padding: 10px 10px 10px 0px; font-size: 56px; line-height: 36px; color: rgb(0, 136, 72); font-weight: normal; display: inline-block; vertical-align: middle;\">4</dropcaps>Thành phẩm</h3><p class=\"ce-element ce-element--type-text\" =\"\"=\"\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Bánh tằm bì \" class=\"imgcontent lazy initial loaded\" data-id=\"6\" title=\"Bánh tằm bì \" data-src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-thom-beo-dai-chuan-vi-mien-tay-202201051639478503.jpeg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-thom-beo-dai-chuan-vi-mien-tay-202201051639478503.jpeg\" data-was-processed=\"true\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p>', 'img/products/banhtambi1.jpg', '/img/products/banhtambi2.jpg', '/img/products/banhtambi3.jpg', 1, '2022-10-23 18:14:57'),
(71.43, 43, 1, 1, 'Bánh tằm bì', '<p><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"> Bánh tằm dai dai, bì heo bùi bùi sựt sựt, hương thơm từ rau sống và độ giòn từ dưa leo. Khi ăn thì chan nước cốt dừa lên rồi trộn đều sẽ tạo nên món ăn làm hài lòng thực khách.</span><br></p>', '<h4 class=\"ce-element ce-element--type-text\" id=\"2hchildmenuid1\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\">Bước 1: Nhào bột</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\" Nhào bột\" class=\"imgcontent\" data-id=\"6\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-thom-beo-dai-chuan-vi-mien-tay-202201061539009763.jpg\" title=\" Nhào bột\" width=\"360\" height=\"140\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Trộn đều bột gạo, bột nếp và bột năng lại với nhau. Sau đó, đổ từ từ nước sôi đã chuẩn bị vào bột, khuấy đều, rồi dùng tay nhồi tới khi được hỗn hợp đồng nhất, mịn và không dính tay.</p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Xong thì lấy một lượng bột vừa đủ để vào lòng bàn tay, rồi dùng hai tay se lại tạo thành những sợi vừa ăn.</p><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><i style=\"margin: 0px; padding: 0px;\"><quote style=\"text-align: justify; margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 2px solid rgb(195, 229, 248); border-radius: 5px; background-color: rgb(219, 237, 249); line-height: 22px;\">Lưu ý, bạn nên phủ một ít bột áo để các sợi bánh không bị dính vào nhau nhé.</quote></i></p><h4 class=\"ce-element ce-element--type-text\" id=\"2hchildmenuid2\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\">Bước 2: Luộc bánh</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Luộc bánh\" class=\"imgcontent lazy initial loaded\" data-id=\"2\" title=\"Luộc bánh\" data-src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260921325522.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260921325522.jpg\" data-was-processed=\"true\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bắc nồi nước lên bếp, khi nước sôi thì thả các sợi bánh vào. Ta sẽ luộc đến khi thấy chúng nổi lên trên mặt và bột chuyển sang trong hơn thì vớt ra cho ngay vào tô nước đá nha.</p><h4 class=\"ce-element ce-element--type-text\" id=\"2hchildmenuid3\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\">Bước 3: Làm xốt</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Làm xốt\" class=\"imgcontent lazy initial loaded\" data-id=\"3\" title=\"Làm xốt\" data-src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260921514512.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260921514512.jpg\" data-was-processed=\"true\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Trước hết, cho khoảng&nbsp;<b style=\"margin: 0px; padding: 0px;\">30ml nước vào bột bắp</b>, khuấy đều đến khi tan hoàn toàn. Sau đó, bắc nồi lên bếp,&nbsp;<b style=\"margin: 0px; padding: 0px;\">cho hỗn hợp bột bắp cùng nước cốt dừa và ½ muỗng cà phê muối</b>&nbsp;vào. Cuối cùng, hãy nấu đến khi hỗn hợp sôi và sánh, sệt lại thì tắt bếp. Vậy là xong rồi đấy.</p><h4 class=\"ce-element ce-element--type-text\" id=\"2hchildmenuid4\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\">Bước 4: Làm bì</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Làm bì\" class=\"imgcontent lazy initial loaded\" data-id=\"4\" title=\"Làm bì\" data-src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260922143319.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260922143319.jpg\" data-was-processed=\"true\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Trước hết, lấy thịt nạc vai rửa sạch, rồi đem ướp cùng hành tím băm, 1 muỗng cà phê tỏi băm, ½ muỗng cà phê đường, 1 muỗng cà phê hạt nêm, 1 muỗng cà phê tiêu và 1 muỗng canh nước mắm. Trộn đều và để yên tầm 10 – 15 phút.</p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bắc chảo cùng dầu ăn lên bếp, khi dầu nóng thì cho thịt vào, chiên vàng đều. Sau đó, rót nước dừa vô, rim đến khi nước sền sệt thì tắt bếp. Xong thì cắt thịt và da heo luộc (đã chuẩn bị sẵn) thành sợi vừa ăn. Trộn đều cả hai cùng với thính gạo, vậy là có bì rồi nè.</p><h4 class=\"ce-element ce-element--type-text\" id=\"2hchildmenuid5\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\">Bước 5: Làm nước mắm</b></h4><p class=\"ce-element ce-element--type-text\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\" Làm nước mắm\" class=\"imgcontent lazy initial loaded\" data-id=\"5\" title=\" Làm nước mắm\" data-src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260922323798.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-dam-da-chuan-vi-mien-tay-nam-bo-202006260922323798.jpg\" data-was-processed=\"true\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Cho vào chén nước cốt chanh,&nbsp;<b style=\"margin: 0px; padding: 0px;\">70ml nước mắm, 60g đường cùng 80ml nước lọc</b>. Khuấy đều đến khi hòa tan hoàn toàn thì thêm&nbsp;<b style=\"margin: 0px; padding: 0px;\">½ muỗng cà phê tỏi băm và ớt băm</b>&nbsp;vào là được.</p><p class=\"ce-element ce-element--type-text\" style=\"text-align: justify; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\">Tham khảo</b>:&nbsp;<a href=\"https://www.bachhoaxanh.com/kinh-nghiem-hay/cach-lam-banh-tam-khoai-mi-dan-da-deo-mem-thom-ngon-nuoc-cot-dua-1272402\" target=\"_blank\" style=\"margin: 0px; padding: 0px; color: rgb(0, 100, 194);\">Cách làm bánh tằm khoai mì dẻo mềm thơm ngon hấp dẫn</a></p><h3 class=\"ce-element ce-element--type-text\" id=\"hmenuid4\" style=\"text-align: justify; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-weight: bold; font-stretch: normal; font-size: 18px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><dropcaps style=\"margin: 0px; padding: 10px 10px 10px 0px; font-size: 56px; line-height: 36px; color: rgb(0, 136, 72); font-weight: normal; display: inline-block; vertical-align: middle;\">4</dropcaps>Thành phẩm</h3><p class=\"ce-element ce-element--type-text\" =\"\"=\"\" style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Bánh tằm bì \" class=\"imgcontent lazy initial loaded\" data-id=\"6\" title=\"Bánh tằm bì \" data-src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-thom-beo-dai-chuan-vi-mien-tay-202201051639478503.jpeg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/06/26/1265678/cach-lam-banh-tam-bi-thom-beo-dai-chuan-vi-mien-tay-202201051639478503.jpeg\" data-was-processed=\"true\" style=\"text-align: justify; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p>', 'img/products/banhtambi1.jpg', '/img/products/banhtambi2.jpg', '/img/products/banhtambi3.jpg', 1, '2022-10-23 18:14:57');
INSERT INTO `nguoidung_sanpham` (`MaSP`, `MaND`, `MaQuyen`, `MaDM`, `TenSP`, `MoTa`, `CachLam`, `HinhAnh1`, `HinhAnh2`, `HinhAnh3`, `TrangThai`, `ThoiGian`) VALUES
(72.23, 23, 1, 2, 'Gà hấp muối sả', '<p><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">Thơm lừng, lớp da dai giòn, thơm mùi sả, lớp thịt ngọt mềm sẽ là là bữa trưa tuyệt vời cho bạn. Với cách thực hiện siêu đơn giản, </span><a title=\"món gà \" href=\"https://www.dienmayxanh.com/vao-bep/mon-tu-ga\" target=\"_blank\" rel=\"noopener\" style=\"margin: 0px; padding: 0px; transition: all 0.2s ease 0s; color: rgb(40, 138, 214); font-family: Arial, Helvetica, sans-serif; font-size: 18px; background-color: rgb(255, 255, 255);\">món gà</a><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"> này chắc chắn sẽ không làm bạn tốn quá nhiều thời gian.</span><br></p>', '<h4 id=\"1hchildmenuid1\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 1</steps>&nbsp;Sơ chế thịt gà</b></h4><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Để tiết kiệm thời gian hơn thì bạn nên mua gà làm sẵn, gà khi mua về bạn<b style=\"margin: 0px; padding: 0px;\">&nbsp;làm sạch cả trong lẫn ngoài.</b></p><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Để khử bớt mùi tanh thì bạn nên&nbsp;<b style=\"margin: 0px; padding: 0px;\">dùng muối hột chà xát bên ngoài rồi rửa lại với nước sạch nhiều lần</b>&nbsp;cho ra bớt chất nhớt, sau đó rửa sạch lại và để ráo.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><quote class=\"quoteGreen\" style=\"text-align: left; margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 1px solid rgb(3, 120, 65); border-radius: 5px; background-color: rgb(241, 255, 249); line-height: 22px; text-indent: 20px; position: relative;\"><b style=\"margin: 0px; padding: 0px;\">Mẹo hay:</b>&nbsp;Ngoài cách dùng muối hột chà xát để khử mùi tanh, thì bạn có thể&nbsp;<b style=\"margin: 0px; padding: 0px;\">chà xát và ngâm gà từ 3-5 phút trong hỗn hợp muối, giấm hoặc muối và nước cốt chanh.</b></quote></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Sơ chế thịt gà\" class=\"imgcontent lazy initial loaded\" data-id=\"5\" title=\"Sơ chế thịt gà\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151119208532.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151119208532.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h4 id=\"1hchildmenuid2\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 2</steps>&nbsp;Sơ chế các nguyên liệu khác</b></h4><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Sả thì bạn cắt bỏ gốc, rửa sạch sẽ rồi dùng<b style=\"margin: 0px; padding: 0px;\">&nbsp;2 cây cắt thành khúc ngắn khoảng 1 ngón tay và đập dập, 2 cây sả còn lại băm nhuyễn.</b></p><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Hành tím bóc sạch vỏ, dùng&nbsp;<b style=\"margin: 0px; padding: 0px;\">2 củ băm nhỏ, 2 củ thái lát</b>. Gừng thì bạn&nbsp;<b style=\"margin: 0px; padding: 0px;\">cạo vỏ cho sạch, cắt thành sợi.</b>&nbsp;Trong khi đó ớt thì&nbsp;<b style=\"margin: 0px; padding: 0px;\">lặt cuống, rửa sạch, dùng 1 trái băm nhỏ, 1 trái cắt lát.</b></p><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Rau răm bạn&nbsp;<b style=\"margin: 0px; padding: 0px;\">nhặt bỏ lá hư, già, rửa sạch và để ráo</b>. Lấy 5 lá chanh<b style=\"margin: 0px; padding: 0px;\">&nbsp;rửa sạch rồi cắt sợi nhỏ.</b>&nbsp;Bạn dùng 1 quả chanh&nbsp;<b style=\"margin: 0px; padding: 0px;\">vắt nước cốt rồi lấy vỏ cắt sợi nhỏ.</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Sơ chế các nguyên liệu khác\" class=\"imgcontent lazy initial loaded\" data-id=\"6\" title=\"Sơ chế các nguyên liệu khác\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151120280004.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151120280004.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h4 id=\"1hchildmenuid3\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 3</steps>&nbsp;Ướp thịt gà</b></h4><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Khi gà đã ráo nước thì bạn<b style=\"margin: 0px; padding: 0px;\">&nbsp;</b>ướp gà với&nbsp;<b style=\"margin: 0px; padding: 0px;\">1/2 muỗng canh đường, 1 muỗng cà phê tiêu xay, 1/2 muỗng canh hạt nêm, 1/2 muỗng canh nước mắm và phần hành tím, ớt, sả, băm</b>, sau đó trộn đều.</p><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bạn tiến hành<b style=\"margin: 0px; padding: 0px;\">&nbsp;khứa vài đường trên mình gà, rồi chà xát hỗn hợp lên thân gà</b>&nbsp;để ướp trong khoảng&nbsp;<b style=\"margin: 0px; padding: 0px;\">30 phút.</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Ướp thịt gà\" class=\"imgcontent lazy initial loaded\" data-id=\"7\" title=\"Ướp thịt gà\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151121162548.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151121162548.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h4 id=\"1hchildmenuid4\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 4</steps>&nbsp;Hấp gà</b></h4><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Trước tiên, bạn cho khoảng 400g muối hột vào&nbsp;<b style=\"margin: 0px; padding: 0px;\">nồi rang khoảng 6-7 phút</b>&nbsp;rồi&nbsp;đặt phần đuôi sả lên trên.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"rang muối hột\" class=\"imgcontent lazy initial loaded\" data-id=\"2\" title=\"rang muối hột\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202008261526499501.gif\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202008261526499501.gif\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Kế đến bạn cho gà vào nồi,<b style=\"margin: 0px; padding: 0px;\">&nbsp;thêm rau răm, hành tím, ớt cắt lát, gừng và vỏ chanh cắt sợi</b>&nbsp;vào hấp chín trong<b style=\"margin: 0px; padding: 0px;\">&nbsp;khoảng 30 phút</b>&nbsp;ở lửa vừa.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\" Hấp gà\" class=\"imgcontent lazy initial loaded\" data-id=\"8\" title=\" Hấp gà\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151122033453.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151122033453.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h4 id=\"1hchildmenuid5\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 5</steps>&nbsp;Pha nước chấm</b></h4><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bạn cho vào cối&nbsp;<b style=\"margin: 0px; padding: 0px;\">1/2 muỗng canh muối hột, 1/2 muỗng cà phê bột ngọt, 1 muỗng canh đường, 1 muỗng cà phê tiêu xay, ớt băm, lá chanh</b>&nbsp;và giã nhẹ cho các gia vị được trộn đều, sau đó&nbsp;<b style=\"margin: 0px; padding: 0px;\">thêm nước cốt chanh</b>&nbsp;vào khuấy đều lần nữa.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Pha muối ăn kèm\" class=\"imgcontent lazy initial loaded\" data-id=\"4\" title=\"Pha muối ăn kèm\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202008261528055322.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202008261528055322.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h4 id=\"1hchildmenuid6\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 6</steps>&nbsp;Thành phẩm</b></h4><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Thịt gà hấp nóng hổi, lớp da gà vàng ươm trông rất hấp dẫn. Bạn hãy dọn món gà này ra cùng chén muối vừa làm xong nhé.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Thành phẩm\" class=\"imgcontent lazy initial loaded\" data-id=\"11\" title=\"Thành phẩm\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202205251523135929.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202205251523135929.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h3 id=\"hmenuid3\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-weight: bold; font-stretch: normal; font-size: 18px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><dropcaps style=\"margin: 0px; padding: 10px 10px 10px 0px; font-size: 56px; line-height: 36px; color: rgb(0, 136, 72); font-weight: normal; display: inline-block; vertical-align: middle;\">3</dropcaps>Thưởng thức</h3><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Gà hấp muối hột vừa thơm,<b style=\"margin: 0px; padding: 0px;\">&nbsp;thịt gà mềm và mọng nước, chấm với muối ớt chanh</b>&nbsp;thì còn gì bằng. Với&nbsp;<a bis_skin_checked=\"1\" href=\"https://www.bachhoaxanh.com/kinh-nghiem-hay/mon-ngon-moi-ngay/1730\" target=\"_blank\" style=\"margin: 0px; padding: 0px; color: rgb(0, 100, 194);\">món ăn</a>&nbsp;này, bạn không nên chặt gà mà nên xé gà để thưởng thức trọn vẹn hương vị của từng thớ thịt thơm ngọt trên đầu lưỡi.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><quote style=\"text-align: left; margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 2px solid rgb(195, 229, 248); border-radius: 5px; background-color: rgb(219, 237, 249); line-height: 22px;\"><b style=\"margin: 0px; padding: 0px;\">Tham khảo thêm:&nbsp;</b><a href=\"https://www.bachhoaxanh.com/kinh-nghiem-hay/cach-lam-muoi-sa-ot-don-gian-vi-cay-nong-cuc-hao-com-1374686\" target=\"_blank\" style=\"margin: 0px; padding: 0px; color: rgb(0, 100, 194);\">Cách làm muối sả ớt đơn giản, vị cay nồng cực hao cơm</a></quote></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Thưởng thức gà hấp muối sả\" class=\"imgcontent lazy initial loaded\" data-id=\"5\" title=\"Thưởng thức gà hấp muối sả\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202008261529052345.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202008261529052345.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h3 id=\"hmenuid4\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-weight: bold; font-stretch: normal; font-size: 18px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><dropcaps style=\"margin: 0px; padding: 10px 10px 10px 0px; font-size: 56px; line-height: 36px; color: rgb(0, 136, 72); font-weight: normal; display: inline-block; vertical-align: middle;\">3</dropcaps>Lưu ý khi làm gà hấp muối sả</h3><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Đối với món gà hấp muối sả, bạn<b style=\"margin: 0px; padding: 0px;\">&nbsp;tuyệt đối không cho nước vào hấp. Không nên dùng muối tinh hay muối i-ốt để thay thế muối hột,</b>&nbsp;vì các loại muối này sẽ ngấm vào thịt gà làm cho thịt gà mặn và mất ngon.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Lưu ý khi làm gà hấp muối sả\" class=\"imgcontent lazy initial loaded\" data-id=\"9\" title=\"Lưu ý khi làm gà hấp muối sả\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151130042321.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151130042321.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p>', 'img/products/gahapmuoisa1.jpg', '/img/products/gahapmuoisa2.jpg', '/img/products/gahapmuoisa3.jpg', 1, '2022-10-23 18:15:29'),
(72.26, 26, 1, 2, 'Gà hấp muối sả', '<p><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">Thơm lừng, lớp da dai giòn, thơm mùi sả, lớp thịt ngọt mềm sẽ là là bữa trưa tuyệt vời cho bạn. Với cách thực hiện siêu đơn giản, </span><a title=\"món gà \" href=\"https://www.dienmayxanh.com/vao-bep/mon-tu-ga\" target=\"_blank\" rel=\"noopener\" style=\"margin: 0px; padding: 0px; transition: all 0.2s ease 0s; color: rgb(40, 138, 214); font-family: Arial, Helvetica, sans-serif; font-size: 18px; background-color: rgb(255, 255, 255);\">món gà</a><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"> này chắc chắn sẽ không làm bạn tốn quá nhiều thời gian.</span><br></p>', '<h4 id=\"1hchildmenuid1\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 1</steps>&nbsp;Sơ chế thịt gà</b></h4><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Để tiết kiệm thời gian hơn thì bạn nên mua gà làm sẵn, gà khi mua về bạn<b style=\"margin: 0px; padding: 0px;\">&nbsp;làm sạch cả trong lẫn ngoài.</b></p><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Để khử bớt mùi tanh thì bạn nên&nbsp;<b style=\"margin: 0px; padding: 0px;\">dùng muối hột chà xát bên ngoài rồi rửa lại với nước sạch nhiều lần</b>&nbsp;cho ra bớt chất nhớt, sau đó rửa sạch lại và để ráo.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><quote class=\"quoteGreen\" style=\"text-align: left; margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 1px solid rgb(3, 120, 65); border-radius: 5px; background-color: rgb(241, 255, 249); line-height: 22px; text-indent: 20px; position: relative;\"><b style=\"margin: 0px; padding: 0px;\">Mẹo hay:</b>&nbsp;Ngoài cách dùng muối hột chà xát để khử mùi tanh, thì bạn có thể&nbsp;<b style=\"margin: 0px; padding: 0px;\">chà xát và ngâm gà từ 3-5 phút trong hỗn hợp muối, giấm hoặc muối và nước cốt chanh.</b></quote></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Sơ chế thịt gà\" class=\"imgcontent lazy initial loaded\" data-id=\"5\" title=\"Sơ chế thịt gà\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151119208532.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151119208532.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h4 id=\"1hchildmenuid2\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 2</steps>&nbsp;Sơ chế các nguyên liệu khác</b></h4><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Sả thì bạn cắt bỏ gốc, rửa sạch sẽ rồi dùng<b style=\"margin: 0px; padding: 0px;\">&nbsp;2 cây cắt thành khúc ngắn khoảng 1 ngón tay và đập dập, 2 cây sả còn lại băm nhuyễn.</b></p><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Hành tím bóc sạch vỏ, dùng&nbsp;<b style=\"margin: 0px; padding: 0px;\">2 củ băm nhỏ, 2 củ thái lát</b>. Gừng thì bạn&nbsp;<b style=\"margin: 0px; padding: 0px;\">cạo vỏ cho sạch, cắt thành sợi.</b>&nbsp;Trong khi đó ớt thì&nbsp;<b style=\"margin: 0px; padding: 0px;\">lặt cuống, rửa sạch, dùng 1 trái băm nhỏ, 1 trái cắt lát.</b></p><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Rau răm bạn&nbsp;<b style=\"margin: 0px; padding: 0px;\">nhặt bỏ lá hư, già, rửa sạch và để ráo</b>. Lấy 5 lá chanh<b style=\"margin: 0px; padding: 0px;\">&nbsp;rửa sạch rồi cắt sợi nhỏ.</b>&nbsp;Bạn dùng 1 quả chanh&nbsp;<b style=\"margin: 0px; padding: 0px;\">vắt nước cốt rồi lấy vỏ cắt sợi nhỏ.</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Sơ chế các nguyên liệu khác\" class=\"imgcontent lazy initial loaded\" data-id=\"6\" title=\"Sơ chế các nguyên liệu khác\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151120280004.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151120280004.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h4 id=\"1hchildmenuid3\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 3</steps>&nbsp;Ướp thịt gà</b></h4><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Khi gà đã ráo nước thì bạn<b style=\"margin: 0px; padding: 0px;\">&nbsp;</b>ướp gà với&nbsp;<b style=\"margin: 0px; padding: 0px;\">1/2 muỗng canh đường, 1 muỗng cà phê tiêu xay, 1/2 muỗng canh hạt nêm, 1/2 muỗng canh nước mắm và phần hành tím, ớt, sả, băm</b>, sau đó trộn đều.</p><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bạn tiến hành<b style=\"margin: 0px; padding: 0px;\">&nbsp;khứa vài đường trên mình gà, rồi chà xát hỗn hợp lên thân gà</b>&nbsp;để ướp trong khoảng&nbsp;<b style=\"margin: 0px; padding: 0px;\">30 phút.</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Ướp thịt gà\" class=\"imgcontent lazy initial loaded\" data-id=\"7\" title=\"Ướp thịt gà\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151121162548.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151121162548.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h4 id=\"1hchildmenuid4\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 4</steps>&nbsp;Hấp gà</b></h4><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Trước tiên, bạn cho khoảng 400g muối hột vào&nbsp;<b style=\"margin: 0px; padding: 0px;\">nồi rang khoảng 6-7 phút</b>&nbsp;rồi&nbsp;đặt phần đuôi sả lên trên.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"rang muối hột\" class=\"imgcontent lazy initial loaded\" data-id=\"2\" title=\"rang muối hột\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202008261526499501.gif\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202008261526499501.gif\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Kế đến bạn cho gà vào nồi,<b style=\"margin: 0px; padding: 0px;\">&nbsp;thêm rau răm, hành tím, ớt cắt lát, gừng và vỏ chanh cắt sợi</b>&nbsp;vào hấp chín trong<b style=\"margin: 0px; padding: 0px;\">&nbsp;khoảng 30 phút</b>&nbsp;ở lửa vừa.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\" Hấp gà\" class=\"imgcontent lazy initial loaded\" data-id=\"8\" title=\" Hấp gà\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151122033453.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151122033453.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h4 id=\"1hchildmenuid5\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 5</steps>&nbsp;Pha nước chấm</b></h4><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bạn cho vào cối&nbsp;<b style=\"margin: 0px; padding: 0px;\">1/2 muỗng canh muối hột, 1/2 muỗng cà phê bột ngọt, 1 muỗng canh đường, 1 muỗng cà phê tiêu xay, ớt băm, lá chanh</b>&nbsp;và giã nhẹ cho các gia vị được trộn đều, sau đó&nbsp;<b style=\"margin: 0px; padding: 0px;\">thêm nước cốt chanh</b>&nbsp;vào khuấy đều lần nữa.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Pha muối ăn kèm\" class=\"imgcontent lazy initial loaded\" data-id=\"4\" title=\"Pha muối ăn kèm\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202008261528055322.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202008261528055322.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h4 id=\"1hchildmenuid6\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 6</steps>&nbsp;Thành phẩm</b></h4><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Thịt gà hấp nóng hổi, lớp da gà vàng ươm trông rất hấp dẫn. Bạn hãy dọn món gà này ra cùng chén muối vừa làm xong nhé.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Thành phẩm\" class=\"imgcontent lazy initial loaded\" data-id=\"11\" title=\"Thành phẩm\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202205251523135929.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202205251523135929.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h3 id=\"hmenuid3\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-weight: bold; font-stretch: normal; font-size: 18px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><dropcaps style=\"margin: 0px; padding: 10px 10px 10px 0px; font-size: 56px; line-height: 36px; color: rgb(0, 136, 72); font-weight: normal; display: inline-block; vertical-align: middle;\">3</dropcaps>Thưởng thức</h3><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Gà hấp muối hột vừa thơm,<b style=\"margin: 0px; padding: 0px;\">&nbsp;thịt gà mềm và mọng nước, chấm với muối ớt chanh</b>&nbsp;thì còn gì bằng. Với&nbsp;<a bis_skin_checked=\"1\" href=\"https://www.bachhoaxanh.com/kinh-nghiem-hay/mon-ngon-moi-ngay/1730\" target=\"_blank\" style=\"margin: 0px; padding: 0px; color: rgb(0, 100, 194);\">món ăn</a>&nbsp;này, bạn không nên chặt gà mà nên xé gà để thưởng thức trọn vẹn hương vị của từng thớ thịt thơm ngọt trên đầu lưỡi.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><quote style=\"text-align: left; margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 2px solid rgb(195, 229, 248); border-radius: 5px; background-color: rgb(219, 237, 249); line-height: 22px;\"><b style=\"margin: 0px; padding: 0px;\">Tham khảo thêm:&nbsp;</b><a href=\"https://www.bachhoaxanh.com/kinh-nghiem-hay/cach-lam-muoi-sa-ot-don-gian-vi-cay-nong-cuc-hao-com-1374686\" target=\"_blank\" style=\"margin: 0px; padding: 0px; color: rgb(0, 100, 194);\">Cách làm muối sả ớt đơn giản, vị cay nồng cực hao cơm</a></quote></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Thưởng thức gà hấp muối sả\" class=\"imgcontent lazy initial loaded\" data-id=\"5\" title=\"Thưởng thức gà hấp muối sả\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202008261529052345.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202008261529052345.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h3 id=\"hmenuid4\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-weight: bold; font-stretch: normal; font-size: 18px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><dropcaps style=\"margin: 0px; padding: 10px 10px 10px 0px; font-size: 56px; line-height: 36px; color: rgb(0, 136, 72); font-weight: normal; display: inline-block; vertical-align: middle;\">3</dropcaps>Lưu ý khi làm gà hấp muối sả</h3><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Đối với món gà hấp muối sả, bạn<b style=\"margin: 0px; padding: 0px;\">&nbsp;tuyệt đối không cho nước vào hấp. Không nên dùng muối tinh hay muối i-ốt để thay thế muối hột,</b>&nbsp;vì các loại muối này sẽ ngấm vào thịt gà làm cho thịt gà mặn và mất ngon.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Lưu ý khi làm gà hấp muối sả\" class=\"imgcontent lazy initial loaded\" data-id=\"9\" title=\"Lưu ý khi làm gà hấp muối sả\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151130042321.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151130042321.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p>', 'img/products/gahapmuoisa1.jpg', '/img/products/gahapmuoisa2.jpg', '/img/products/gahapmuoisa3.jpg', 1, '2022-10-23 18:15:29');
INSERT INTO `nguoidung_sanpham` (`MaSP`, `MaND`, `MaQuyen`, `MaDM`, `TenSP`, `MoTa`, `CachLam`, `HinhAnh1`, `HinhAnh2`, `HinhAnh3`, `TrangThai`, `ThoiGian`) VALUES
(72.32, 32, 1, 2, 'Gà hấp muối sả', '<p><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">Thơm lừng, lớp da dai giòn, thơm mùi sả, lớp thịt ngọt mềm sẽ là là bữa trưa tuyệt vời cho bạn. Với cách thực hiện siêu đơn giản, </span><a title=\"món gà \" href=\"https://www.dienmayxanh.com/vao-bep/mon-tu-ga\" target=\"_blank\" rel=\"noopener\" style=\"margin: 0px; padding: 0px; transition: all 0.2s ease 0s; color: rgb(40, 138, 214); font-family: Arial, Helvetica, sans-serif; font-size: 18px; background-color: rgb(255, 255, 255);\">món gà</a><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"> này chắc chắn sẽ không làm bạn tốn quá nhiều thời gian.</span><br></p>', '<h4 id=\"1hchildmenuid1\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 1</steps>&nbsp;Sơ chế thịt gà</b></h4><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Để tiết kiệm thời gian hơn thì bạn nên mua gà làm sẵn, gà khi mua về bạn<b style=\"margin: 0px; padding: 0px;\">&nbsp;làm sạch cả trong lẫn ngoài.</b></p><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Để khử bớt mùi tanh thì bạn nên&nbsp;<b style=\"margin: 0px; padding: 0px;\">dùng muối hột chà xát bên ngoài rồi rửa lại với nước sạch nhiều lần</b>&nbsp;cho ra bớt chất nhớt, sau đó rửa sạch lại và để ráo.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><quote class=\"quoteGreen\" style=\"text-align: left; margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 1px solid rgb(3, 120, 65); border-radius: 5px; background-color: rgb(241, 255, 249); line-height: 22px; text-indent: 20px; position: relative;\"><b style=\"margin: 0px; padding: 0px;\">Mẹo hay:</b>&nbsp;Ngoài cách dùng muối hột chà xát để khử mùi tanh, thì bạn có thể&nbsp;<b style=\"margin: 0px; padding: 0px;\">chà xát và ngâm gà từ 3-5 phút trong hỗn hợp muối, giấm hoặc muối và nước cốt chanh.</b></quote></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Sơ chế thịt gà\" class=\"imgcontent lazy initial loaded\" data-id=\"5\" title=\"Sơ chế thịt gà\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151119208532.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151119208532.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h4 id=\"1hchildmenuid2\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 2</steps>&nbsp;Sơ chế các nguyên liệu khác</b></h4><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Sả thì bạn cắt bỏ gốc, rửa sạch sẽ rồi dùng<b style=\"margin: 0px; padding: 0px;\">&nbsp;2 cây cắt thành khúc ngắn khoảng 1 ngón tay và đập dập, 2 cây sả còn lại băm nhuyễn.</b></p><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Hành tím bóc sạch vỏ, dùng&nbsp;<b style=\"margin: 0px; padding: 0px;\">2 củ băm nhỏ, 2 củ thái lát</b>. Gừng thì bạn&nbsp;<b style=\"margin: 0px; padding: 0px;\">cạo vỏ cho sạch, cắt thành sợi.</b>&nbsp;Trong khi đó ớt thì&nbsp;<b style=\"margin: 0px; padding: 0px;\">lặt cuống, rửa sạch, dùng 1 trái băm nhỏ, 1 trái cắt lát.</b></p><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Rau răm bạn&nbsp;<b style=\"margin: 0px; padding: 0px;\">nhặt bỏ lá hư, già, rửa sạch và để ráo</b>. Lấy 5 lá chanh<b style=\"margin: 0px; padding: 0px;\">&nbsp;rửa sạch rồi cắt sợi nhỏ.</b>&nbsp;Bạn dùng 1 quả chanh&nbsp;<b style=\"margin: 0px; padding: 0px;\">vắt nước cốt rồi lấy vỏ cắt sợi nhỏ.</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Sơ chế các nguyên liệu khác\" class=\"imgcontent lazy initial loaded\" data-id=\"6\" title=\"Sơ chế các nguyên liệu khác\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151120280004.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151120280004.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h4 id=\"1hchildmenuid3\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 3</steps>&nbsp;Ướp thịt gà</b></h4><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Khi gà đã ráo nước thì bạn<b style=\"margin: 0px; padding: 0px;\">&nbsp;</b>ướp gà với&nbsp;<b style=\"margin: 0px; padding: 0px;\">1/2 muỗng canh đường, 1 muỗng cà phê tiêu xay, 1/2 muỗng canh hạt nêm, 1/2 muỗng canh nước mắm và phần hành tím, ớt, sả, băm</b>, sau đó trộn đều.</p><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bạn tiến hành<b style=\"margin: 0px; padding: 0px;\">&nbsp;khứa vài đường trên mình gà, rồi chà xát hỗn hợp lên thân gà</b>&nbsp;để ướp trong khoảng&nbsp;<b style=\"margin: 0px; padding: 0px;\">30 phút.</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Ướp thịt gà\" class=\"imgcontent lazy initial loaded\" data-id=\"7\" title=\"Ướp thịt gà\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151121162548.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151121162548.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h4 id=\"1hchildmenuid4\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 4</steps>&nbsp;Hấp gà</b></h4><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Trước tiên, bạn cho khoảng 400g muối hột vào&nbsp;<b style=\"margin: 0px; padding: 0px;\">nồi rang khoảng 6-7 phút</b>&nbsp;rồi&nbsp;đặt phần đuôi sả lên trên.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"rang muối hột\" class=\"imgcontent lazy initial loaded\" data-id=\"2\" title=\"rang muối hột\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202008261526499501.gif\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202008261526499501.gif\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Kế đến bạn cho gà vào nồi,<b style=\"margin: 0px; padding: 0px;\">&nbsp;thêm rau răm, hành tím, ớt cắt lát, gừng và vỏ chanh cắt sợi</b>&nbsp;vào hấp chín trong<b style=\"margin: 0px; padding: 0px;\">&nbsp;khoảng 30 phút</b>&nbsp;ở lửa vừa.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\" Hấp gà\" class=\"imgcontent lazy initial loaded\" data-id=\"8\" title=\" Hấp gà\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151122033453.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151122033453.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h4 id=\"1hchildmenuid5\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 5</steps>&nbsp;Pha nước chấm</b></h4><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bạn cho vào cối&nbsp;<b style=\"margin: 0px; padding: 0px;\">1/2 muỗng canh muối hột, 1/2 muỗng cà phê bột ngọt, 1 muỗng canh đường, 1 muỗng cà phê tiêu xay, ớt băm, lá chanh</b>&nbsp;và giã nhẹ cho các gia vị được trộn đều, sau đó&nbsp;<b style=\"margin: 0px; padding: 0px;\">thêm nước cốt chanh</b>&nbsp;vào khuấy đều lần nữa.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Pha muối ăn kèm\" class=\"imgcontent lazy initial loaded\" data-id=\"4\" title=\"Pha muối ăn kèm\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202008261528055322.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202008261528055322.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h4 id=\"1hchildmenuid6\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><b style=\"margin: 0px; padding: 0px;\"><steps style=\"margin: 0px 5px 0px 0px; padding: 5px; background-color: rgb(30, 180, 110); border-radius: 5px; color: rgb(255, 255, 255);\">Bước 6</steps>&nbsp;Thành phẩm</b></h4><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Thịt gà hấp nóng hổi, lớp da gà vàng ươm trông rất hấp dẫn. Bạn hãy dọn món gà này ra cùng chén muối vừa làm xong nhé.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Thành phẩm\" class=\"imgcontent lazy initial loaded\" data-id=\"11\" title=\"Thành phẩm\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202205251523135929.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202205251523135929.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h3 id=\"hmenuid3\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-weight: bold; font-stretch: normal; font-size: 18px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><dropcaps style=\"margin: 0px; padding: 10px 10px 10px 0px; font-size: 56px; line-height: 36px; color: rgb(0, 136, 72); font-weight: normal; display: inline-block; vertical-align: middle;\">3</dropcaps>Thưởng thức</h3><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Gà hấp muối hột vừa thơm,<b style=\"margin: 0px; padding: 0px;\">&nbsp;thịt gà mềm và mọng nước, chấm với muối ớt chanh</b>&nbsp;thì còn gì bằng. Với&nbsp;<a bis_skin_checked=\"1\" href=\"https://www.bachhoaxanh.com/kinh-nghiem-hay/mon-ngon-moi-ngay/1730\" target=\"_blank\" style=\"margin: 0px; padding: 0px; color: rgb(0, 100, 194);\">món ăn</a>&nbsp;này, bạn không nên chặt gà mà nên xé gà để thưởng thức trọn vẹn hương vị của từng thớ thịt thơm ngọt trên đầu lưỡi.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><quote style=\"text-align: left; margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 2px solid rgb(195, 229, 248); border-radius: 5px; background-color: rgb(219, 237, 249); line-height: 22px;\"><b style=\"margin: 0px; padding: 0px;\">Tham khảo thêm:&nbsp;</b><a href=\"https://www.bachhoaxanh.com/kinh-nghiem-hay/cach-lam-muoi-sa-ot-don-gian-vi-cay-nong-cuc-hao-com-1374686\" target=\"_blank\" style=\"margin: 0px; padding: 0px; color: rgb(0, 100, 194);\">Cách làm muối sả ớt đơn giản, vị cay nồng cực hao cơm</a></quote></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Thưởng thức gà hấp muối sả\" class=\"imgcontent lazy initial loaded\" data-id=\"5\" title=\"Thưởng thức gà hấp muối sả\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202008261529052345.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202008261529052345.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><h3 id=\"hmenuid4\" style=\"text-align: left; margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-weight: bold; font-stretch: normal; font-size: 18px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 753px;\"><dropcaps style=\"margin: 0px; padding: 10px 10px 10px 0px; font-size: 56px; line-height: 36px; color: rgb(0, 136, 72); font-weight: normal; display: inline-block; vertical-align: middle;\">3</dropcaps>Lưu ý khi làm gà hấp muối sả</h3><p style=\"text-align: left; margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Đối với món gà hấp muối sả, bạn<b style=\"margin: 0px; padding: 0px;\">&nbsp;tuyệt đối không cho nước vào hấp. Không nên dùng muối tinh hay muối i-ốt để thay thế muối hột,</b>&nbsp;vì các loại muối này sẽ ngấm vào thịt gà làm cho thịt gà mặn và mất ngon.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Lưu ý khi làm gà hấp muối sả\" class=\"imgcontent lazy initial loaded\" data-id=\"9\" title=\"Lưu ý khi làm gà hấp muối sả\" data-src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151130042321.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2020/08/26/1284013/cach-lam-ga-hap-muoi-sa-thit-ga-mong-nuoc-thom-ngon-kho-cuong-202203151130042321.jpg\" data-was-processed=\"true\" style=\"text-align: left; margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p>', 'img/products/gahapmuoisa1.jpg', '/img/products/gahapmuoisa2.jpg', '/img/products/gahapmuoisa3.jpg', 1, '2022-10-23 18:15:29'),
(75.43, 43, 1, 3, 'Sườn kho trứng cút', '<p><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">Những miếng sườn với màu vàng thật cuốn hút đã khiến chúng ta không thể làm ngơ, sườn kho mềm mềm đậm đà gia vị, đã thế còn ngọt thanh từ </span><a title=\"nước dừa tươi\" href=\"https://www.dienmayxanh.com/vao-bep/10-tac-dung-tuyet-voi-cua-nuoc-dua-ban-nen-biet-05105\" target=\"_blank\" rel=\"noopener\" style=\"margin: 0px; padding: 0px; transition: all 0.2s ease 0s; color: rgb(40, 138, 214); font-family: Arial, Helvetica, sans-serif; font-size: 18px; background-color: rgb(255, 255, 255);\">nước dừa tươi</a><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">, chấm phá thêm cái béo béo từ </span><a title=\"trứng cút\" href=\"https://www.dienmayxanh.com/vao-bep/an-trung-cut-co-tac-dung-gi-luu-y-khi-an-va-cac-mon-ngon-tu-05786\" target=\"_blank\" rel=\"noopener\" style=\"margin: 0px; padding: 0px; transition: all 0.2s ease 0s; color: rgb(40, 138, 214); font-family: Arial, Helvetica, sans-serif; font-size: 18px; background-color: rgb(255, 255, 255);\">trứng cút</a><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"> đúng là quá tuyệt vời.</span><br></p>', '<p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\">Bước 1: Sơ chế nguyên liệu</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Sơ chế nguyên liệu\" class=\"imgcontent\" data-id=\"1\" src=\"https://cdn.tgdd.vn/Files/2021/01/20/1321431/ngan-thit-kho-trung-thu-ngay-mon-suon-non-kho-trung-cut-vua-la-mieng-lai-cuc-ki-thom-ngon-202101201444483628.jpg\" title=\"Sơ chế nguyên liệu\" width=\"360\" height=\"140\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; display: block; border-radius: 4px;\"></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Sườn non sau khi mua về chúng ta chặt ra từng miếng vừa ăn, sau đó <b style=\"margin: 0px; padding: 0px;\">trần qua nước nóng</b> để loại bỏ máu và chất bẩn, rửa sạch và để ráo</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Lấy một cái nồi, bỏ trứng cút vào luộc, trứng cút ta có thể mua loại luộc sẵn để tiết kiệm thời gian. Nếu bạn mua trứng chưa luộc thì đem luộc chín, sau đó bóc sạch bỏ. Để riêng.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Hành tím, tỏi lột vỏ và băm nhuyễn.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\">Bước 2 : Ướp thịt</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Ướp thịt\" class=\"imgcontent lazy initial loaded\" data-id=\"2\" title=\"Ướp thịt\" data-src=\"https://cdn.tgdd.vn/Files/2021/01/20/1321431/ngan-thit-kho-trung-thu-ngay-mon-suon-non-kho-trung-cut-vua-la-mieng-lai-cuc-ki-thom-ngon-202101201445092339.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/01/20/1321431/ngan-thit-kho-trung-thu-ngay-mon-suon-non-kho-trung-cut-vua-la-mieng-lai-cuc-ki-thom-ngon-202101201445092339.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Cho sườn non vào một cái bát, cho vào 1 muỗng canh bột nêm, xíu bột ngọt, 3 muỗng canh nước mắm, 1 muỗng dầu dừa tạo màu, 1 muỗng canh đường vàng và trộn đều, để trong <b style=\"margin: 0px; padding: 0px;\">khoảng 30 phút cho sườn ngấm gia vị</b>.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\">Bước 3: Kho Thịt</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Kho Thịt\" class=\"imgcontent lazy initial loaded\" data-id=\"3\" title=\"Kho Thịt\" data-src=\"https://cdn.tgdd.vn/Files/2021/01/20/1321431/ngan-thit-kho-trung-thu-ngay-mon-suon-non-kho-trung-cut-vua-la-mieng-lai-cuc-ki-thom-ngon-202101201445330558.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2021/01/20/1321431/ngan-thit-kho-trung-thu-ngay-mon-suon-non-kho-trung-cut-vua-la-mieng-lai-cuc-ki-thom-ngon-202101201445330558.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 50%; opacity: 1; display: block; border-radius: 4px;\"></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Tiếp theo, cho hành tím vào để phi. Sau khi nghe được mùi thơm của hành, ta cho trứng vào rim đến khi trứng ngả màu vàng cánh gián, tiếp đó <b style=\"margin: 0px; padding: 0px;\">bỏ sườn vào đảo đều và hầm 30 phút</b>, Nếu sợ nước cạn, bạn có thể cho thêm 1 thìa nhỏ nước lọc vào đun cùng,. Hầm đến khi thịt mềm là được rồi nha</p>', 'img/products/suonkhotrungcut1.jpg', '/img/products/suonkhotrungcut2.jpg', '/img/products/suonkhotrungcut3.jpg', 1, '2022-10-23 18:16:07');
INSERT INTO `nguoidung_sanpham` (`MaSP`, `MaND`, `MaQuyen`, `MaDM`, `TenSP`, `MoTa`, `CachLam`, `HinhAnh1`, `HinhAnh2`, `HinhAnh3`, `TrangThai`, `ThoiGian`) VALUES
(7538, 25, 2, 1, 'Cháo trắng', '<p><a href=\"https://www.bachhoaxanh.com/chao-an-lien\" target=\"_blank\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 100, 194); font-family: OpenSans-Regular, Arial, sans-serif; font-weight: 400; text-align: justify; background-color: rgb(255, 255, 255);\">Cháo</a><span style=\"color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif; text-align: justify;\">&nbsp;là món ăn rất phổ biến ở Việt Nam và các nước châu Á khác. Tuy phổ biến nhưng không phải ai cũng biết cách để nấu ra một nồi cháo thơm ngon, gạo nở mềm đều, nhừ mà lại không dính cháo.</span><br></p>', '<h3 id=\"hmenuid1\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 760px; position: relative;\"><dropcaps class=\"firstDropcaps\" style=\"margin: 0px; padding: 10px 10px 10px 0px; font-size: 56px; line-height: 36px; color: rgb(0, 136, 72); font-weight: normal; display: inline-block; vertical-align: middle;\">1</dropcaps>Trộn gạo nếp với một ít gạo tẻ</h3><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Trộn gạo nếp với một ít gạo tẻ\" class=\"imgcontent\" data-id=\"1\" src=\"https://cdn.tgdd.vn/Files/2017/07/16/1003592/bi-quyet-nau-chao-thom-ngon-nhanh-nhu-khong-dinh-noi-202101241245315467.jpg\" title=\"Trộn gạo nếp với một ít gạo tẻ\" width=\"360\" height=\"140\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 760px; height: auto !important; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 760px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Trộn gạo nếp với một ít gạo tẻ</span></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Công đoạn này sẽ giúp cho món cháo của bạn có hương vị thơm ngon hơn nhiều so với cách nấu thông thường. Gạo nếp với hương thơm đặc trưng, khi trộn vào sẽ giúp cho món cháo của bạn có độ sánh dẻo, mềm mịn hơn.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Nấu cháo trộn gạo nếp với gạo tẻ sẽ cho món cháo hương vị thêm thơm ngon, bạn cũng có thể&nbsp;<a href=\"https://www.bachhoaxanh.com/kinh-nghiem-hay/co-nen-nau-com-bang-gao-nep-tron-voi-gao-te-1321197\" target=\"_blank\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 100, 194);\">kết hợp gạo nếp với gạo tẻ để nấu cơm</a>, sẽ làm cho mẻ cơm của bạn ngon hơn. Vừa có độ tơi xốp của gạo tẻ, vừa có độ dẻo của gạo nếp</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><quote class=\"quoteGreen\" style=\"margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 1px solid rgb(3, 120, 65); border-radius: 5px; background-color: rgb(241, 255, 249); line-height: 22px; text-indent: 20px; position: relative;\"><b style=\"margin: 0px; padding: 0px;\">Lưu ý:&nbsp;</b>Nên trộn gạo với tỷ lệ phù hợp, tốt nhất là&nbsp;<b style=\"margin: 0px; padding: 0px;\">1 chén gạo tẻ trộn với 1 nắm gạo nếp</b>&nbsp;bởi vì cho quá nhiều gạo nếp sẽ làm cho cháo bị đặc quánh, ăn bị ngán.</quote></p><h3 id=\"hmenuid2\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 760px;\"><dropcaps style=\"margin: 0px; padding: 10px 10px 10px 0px; font-size: 56px; line-height: 36px; color: rgb(0, 136, 72); font-weight: normal; display: inline-block; vertical-align: middle;\">2</dropcaps>Ngâm gạo 30 phút trước khi nấu</h3><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Trước khi nấu, bạn hãy ngâm gạo với nước lạnh tầm 30 phút\" class=\"imgcontent lazy initial loaded\" data-id=\"2\" title=\"Trước khi nấu, bạn hãy ngâm gạo với nước lạnh tầm 30 phút\" data-src=\"https://cdn.tgdd.vn/Files/2017/07/16/1003592/bi-quyet-nau-chao-thom-ngon-nhanh-nhu-khong-dinh-noi-202101241246434807.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2017/07/16/1003592/bi-quyet-nau-chao-thom-ngon-nhanh-nhu-khong-dinh-noi-202101241246434807.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 760px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 760px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Trước khi nấu, bạn hãy ngâm gạo với nước lạnh tầm 30 phút</span></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Trước khi nấu, bạn hãy&nbsp;<b style=\"margin: 0px; padding: 0px;\">ngâm gạo với nước lạnh tầm 30 phút</b>. Công đoạn ngâm gạo&nbsp;<b style=\"margin: 0px; padding: 0px;\">sẽ giúp cho hạt gạo mềm ra, khi nấu thì cháo sẽ nhừ nhanh hơn.</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><quote class=\"quoteGreen\" style=\"margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 1px solid rgb(3, 120, 65); border-radius: 5px; background-color: rgb(241, 255, 249); line-height: 22px; text-indent: 20px; position: relative;\"><b style=\"margin: 0px; padding: 0px;\">Lưu ý</b>: Khi ngâm không cần phải vo gạo, vo gạo sẽ làm trôi đi các chất dinh dưỡng trên gạo đấy!</quote></p><h3 id=\"hmenuid3\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 760px;\"><dropcaps style=\"margin: 0px; padding: 10px 10px 10px 0px; font-size: 56px; line-height: 36px; color: rgb(0, 136, 72); font-weight: normal; display: inline-block; vertical-align: middle;\">3</dropcaps>Rang gạo trước khi nấu</h3><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Rang gạo hoặc ngâm gạo trước khi nấu\" class=\"imgcontent lazy initial loaded\" data-id=\"3\" title=\"Rang gạo hoặc ngâm gạo trước khi nấu\" data-src=\"https://cdn.tgdd.vn/Files/2017/07/16/1003592/bi-quyet-nau-chao-thom-ngon-nhanh-nhu-khong-dinh-noi-202107311341204045.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2017/07/16/1003592/bi-quyet-nau-chao-thom-ngon-nhanh-nhu-khong-dinh-noi-202107311341204045.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 760px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 760px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Rang gạo hoặc ngâm gạo trước khi nấu</span></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Trước khi nấu cháo, bạn&nbsp;<b style=\"margin: 0px; padding: 0px;\">nên rang&nbsp;<a href=\"https://www.bachhoaxanh.com/gao-gao-nep\" target=\"_blank\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 100, 194);\">gạo</a></b>&nbsp;để cháo nấu&nbsp;<b style=\"margin: 0px; padding: 0px;\">nhanh nhừ nhưng không bị nát mà lại còn dậy mùi thơm khó cưỡng.</b>&nbsp;Lý do là bởi vì trong khi rang, hạt gạo sẽ bị nhiệt độ làm cứng và định hình được hạt gạo, cho dù có nấu lâu thì hạt gạo vẫn giữ được hình dáng chứ không bị nhừ nhuyễn.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Sau khi đã ngâm gạo, hãy cho gạo vào chảo rang trên bếp tới khi&nbsp;<b style=\"margin: 0px; padding: 0px;\">hạt gạo chuyển màu từ trắng đục sang trắng trong</b>&nbsp;thì ngừng lại.</p><h3 id=\"hmenuid4\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 760px;\"><dropcaps style=\"margin: 0px; padding: 10px 10px 10px 0px; font-size: 56px; line-height: 36px; color: rgb(0, 136, 72); font-weight: normal; display: inline-block; vertical-align: middle;\">4</dropcaps>Canh lượng nước phù hợp với món cháo</h3><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Để cháo trắng ngon, không quá lỏng hoặc đặc sệt, bạn nên canh lượng nước với lượng gạo theo tỉ lệ<b style=\"margin: 0px; padding: 0px;\">&nbsp;3 phần nước và 1 phần gạo.</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Với món cháo thịt, cháo cá, hải sản thì tỉ lệ nước và gạo sẽ là&nbsp;<b style=\"margin: 0px; padding: 0px;\">4 phần nước và 1 phần gạo.</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><quote style=\"margin: 0px auto; padding: 10px; display: block; overflow: hidden; border: 2px solid rgb(195, 229, 248); border-radius: 5px; background-color: rgb(219, 237, 249); line-height: 22px;\"><i style=\"margin: 0px; padding: 0px;\"><b style=\"margin: 0px; padding: 0px;\">Xem ngay</b>:&nbsp;<br style=\"margin: 0px; padding: 0px;\"><a href=\"https://www.bachhoaxanh.com/kinh-nghiem-hay/cach-nau-chao-thit-bam-thom-ngon-tai-nha-963548\" rel=\"dofollow\" title=\"Bí quyết nấu cháo thịt bằm thơm ngon đúng điệu\" target=\"_blank\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 100, 194);\">Bí quyết nấu cháo thịt bằm thơm ngon đúng điệu<br style=\"margin: 0px; padding: 0px;\"><br style=\"margin: 0px; padding: 0px;\"></a><a href=\"https://www.bachhoaxanh.com/kinh-nghiem-hay/cach-nau-chao-ca-loc-rau-dang-dam-chat-mien-tay-983325\" target=\"_blank\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 100, 194);\">Cách nấu cháo cá lóc rau đắng, đậm chất miền Tây</a></i></quote></p><h3 id=\"hmenuid5\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 760px;\"><dropcaps style=\"margin: 0px; padding: 10px 10px 10px 0px; font-size: 56px; line-height: 36px; color: rgb(0, 136, 72); font-weight: normal; display: inline-block; vertical-align: middle;\">5</dropcaps>Nấu cháo bằng nước sôi</h3><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Thông thường mọi người sẽ cho gạo vào nồi, cho nước lạnh vào rồi mới bật bếp nấu. Tuy nhiên cách làm này sẽ làm cho hạt cháo nở không đều mà lại còn nồi dễ bị cháy khét ở dưới đáy.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><b style=\"margin: 0px; padding: 0px;\">Cách làm đúng là hãy nấu nước sôi sau đó mới cho gạo vào nấu</b>, đậy kín nắp nồi rồi tắt bếp, sau 15 phút, bạn bật bếp cho nồi cháo sôi lên lần nữa, sau đó vặn nhỏ lửa hoặc tắt bếp để vậy một lúc, cháo sẽ tự nở nhừ.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Nấu cháo theo kiểu này, không những giúp cháo nhanh nhừ mà còn tiết kiệm điện, gas tối đa cho gia đình, gạo sẽ không bám dính vào đáy nồi, không bị cháy.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Nấu cháo bằng nước sôi\" class=\"imgcontent lazy initial loaded\" data-id=\"4\" title=\"Nấu cháo bằng nước sôi\" data-src=\"https://cdn.tgdd.vn/Files/2017/07/16/1003592/bi-quyet-nau-chao-thom-ngon-nhanh-nhu-khong-dinh-noi-202107311341359364.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2017/07/16/1003592/bi-quyet-nau-chao-thom-ngon-nhanh-nhu-khong-dinh-noi-202107311341359364.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 760px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 760px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Nấu cháo bằng nước sôi</span></p><h3 id=\"hmenuid6\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 760px;\"><dropcaps style=\"margin: 0px; padding: 10px 10px 10px 0px; font-size: 56px; line-height: 36px; color: rgb(0, 136, 72); font-weight: normal; display: inline-block; vertical-align: middle;\">6</dropcaps>Cho một ít dầu ăn vào cháo</h3><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Trong quá trình nấu cháo nếu bạn cho vào nồi cháo một ít&nbsp;<a href=\"https://www.bachhoaxanh.com/dau-an\" target=\"_blank\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 100, 194);\">dầu ăn</a>, sau khi chín,&nbsp;<b style=\"margin: 0px; padding: 0px;\">cháo sẽ có vẻ bóng loáng đẹp mắt mà khi ăn cháo cũng dễ ăn hơn.</b></p><h3 id=\"hmenuid7\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 760px;\"><dropcaps style=\"margin: 0px; padding: 10px 10px 10px 0px; font-size: 56px; line-height: 36px; color: rgb(0, 136, 72); font-weight: normal; display: inline-block; vertical-align: middle;\">7</dropcaps>Nấu cháo và các nguyên liệu khác riêng biệt, không nấu chung</h3><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Hãy bỏ thói quen nấu chung cháo với các nguyên liệu khác như&nbsp;<a href=\"https://www.bachhoaxanh.com/ca-tom-muc-ech\" target=\"_blank\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 100, 194);\">hải sản</a>,&nbsp;<a href=\"https://www.bachhoaxanh.com/thit-heo\" target=\"_blank\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 100, 194);\">thịt</a>,&nbsp;<a href=\"https://www.bachhoaxanh.com/rau-cu-rau-nem\" target=\"_blank\" style=\"margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 100, 194);\">rau củ</a>. Nấu kiểu này, cháo đục mà hương vị món cháo cũng không thơm ngon.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bạn nên nấu cháo riêng, nấu thịt, hải sản, rau củ riêng, sau đó khi tất cả các thành phần đều chín, bạn mới cho vào nồi nấu chung ở 10 phút cuối cùng trước khi tắt bếp.&nbsp;<b style=\"margin: 0px; padding: 0px;\">Cháo sẽ trong, ngon, hương vị rõ rệt, không bị lẫn lộn mùi vị.</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\"><img alt=\"Hãy bỏ thói quen nấu chung cháo với các nguyên liệu khác như hải sản, thịt, rau củ\" class=\"imgcontent lazy initial loaded\" data-id=\"5\" title=\"Hãy bỏ thói quen nấu chung cháo với các nguyên liệu khác như hải sản, thịt, rau củ\" data-src=\"https://cdn.tgdd.vn/Files/2017/07/16/1003592/bi-quyet-nau-chao-thom-ngon-nhanh-nhu-khong-dinh-noi-202107311341558518.jpg\" width=\"360\" height=\"140\" src=\"https://cdn.tgdd.vn/Files/2017/07/16/1003592/bi-quyet-nau-chao-thom-ngon-nhanh-nhu-khong-dinh-noi-202107311341558518.jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; border: 0px; transition: all 0.2s ease-in-out 0s; width: 760px; height: auto !important; opacity: 1; display: block; border-radius: 4px;\"><span class=\"caption\" style=\"margin: 0px; padding: 0px; line-height: 28px; width: 760px; text-align: center; font-style: italic; display: block; background: rgb(248, 251, 255);\">Hãy bỏ thói quen nấu chung cháo với các nguyên liệu khác như hải sản, thịt, rau củ</span></p><h3 id=\"hmenuid8\" style=\"margin-right: auto; margin-bottom: 0px; margin-left: auto; padding: 15px 0px 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 1.3; font-family: OpenSans-Regular, Arial, sans-serif; color: rgb(51, 51, 51); outline: none; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; width: 760px;\"><dropcaps style=\"margin: 0px; padding: 10px 10px 10px 0px; font-size: 56px; line-height: 36px; color: rgb(0, 136, 72); font-weight: normal; display: inline-block; vertical-align: middle;\">8</dropcaps>Không khuấy nhiều lần trong quá trình nấu</h3><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Khuấy tưởng chừng là công đoạn giúp cho cháo chín đều, nhừ nhanh hơn nhưng việc này&nbsp;<b style=\"margin: 0px; padding: 0px;\">hoàn toàn là sai lầm!</b></p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Khuấy cháo quá nhiều trong khi nấu sẽ làm cho hạt gạo bị vữa, nát. Ngoài ra nếu bạn đang nấu chung với các nguyên liệu khác thì sẽ làm cho cháo bị tanh.</p><p style=\"margin: 18px auto; padding: 0px; margin-block: 0px; -webkit-font-smoothing: antialiased; text-rendering: geometricprecision; line-height: 28px; text-align: justify; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif;\">Bạn chỉ nên khuấy cháo&nbsp;<b style=\"margin: 0px; padding: 0px;\">2 lần trong quá trình nấu:</b></p><ul style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px; list-style: none; margin-block: 0px; padding-inline-start: 0px; color: rgb(51, 51, 51); font-family: OpenSans-Regular, Arial, sans-serif; font-size: 14px;\"><li style=\"margin: 8px 20px; padding: 0px; font-size: 16px; line-height: 26px; list-style-type: disc;\">Lần thứ nhất là khi mới cho gạo vào nước sôi,&nbsp;<b style=\"margin: 0px; padding: 0px;\">lưu ý chỉ khuấy 1 chiều.</b></li><li style=\"margin: 8px 20px; padding: 0px; font-size: 16px; line-height: 26px; list-style-type: disc;\">Lần thứ hai là khi nấu cháo được 20 - 25 phút, khuấy 1 chiều tầm khoảng 5 phút thì đậy nắp lại và ninh thêm khoảng 3 - 5 phút nữa là cháo đã chín.</li></ul>', 'img/products/chao1.jpg', '/img/products/chao2.jpg', '/img/products/chao3.jpg', 1, '2022-11-12 18:18:38'),
(7544, 26, 2, 1, 'Mỳ Tôm', '<p><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">Mì tôm trứng là một </span><a title=\"món nước\" href=\"https://www.dienmayxanh.com/vao-bep/mon-nuoc\" target=\"_blank\" rel=\"noopener\" style=\"margin: 0px; padding: 0px; text-decoration: none; transition: all 0.2s ease 0s; color: rgb(74, 144, 226); font-family: Arial, Helvetica, sans-serif; font-size: 18px; font-weight: 400; background-color: rgb(255, 255, 255);\">món nước</a><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"> dân giã, cứu đói vào những thời điểm khó khăn, gắn liền với nhiều người bởi sự ngon, tiện lợi và cách chế biến đơn giản</span><br></p>', '<h2 style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 14px 16px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 27.9px; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); outline: none; overflow: hidden;\">Cách chế biến Mì trứng trần</h2><ul style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px; list-style: none; overflow: hidden; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"><li style=\"margin: 0px; padding: 10px 24px; display: block; overflow: hidden;\"><label style=\"margin: 0px 16px 0px 0px; padding: 5px 12px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); outline: none; float: left; background: rgb(221, 221, 221); border-radius: 4px;\">1</label><div class=\"text-method\" style=\"margin: 0px; padding: 0px; overflow: hidden;\"><h3 style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 5px 0px 10px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 27.9px; color: rgb(51, 51, 51); outline: none; overflow: hidden;\">Sơ chế tôm</h3><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; overflow: hidden; color: rgb(51, 51, 51);\">Tôm bạn rửa thật sạch với nước rồi cẩn thận lột bỏ phần vỏ tôm.</p><div class=\"box-gallery\" style=\"margin: -10px 0px 0px; padding: 0px;\"><div class=\"img-method firstItem twoitem\" style=\"margin: 0px 5px 0px 0px; padding: 0px; display: inline-block; overflow: hidden; vertical-align: top; cursor: pointer; border-radius: 4px; width: calc(50% - 5px); float: left;\"><img data-src=\"https://cdn.tgdd.vn/2021/10/CookRecipe/GalleryStep/so-che-tom-7.jpg\" data-picid=\"192442\" class=\"lazy\" alt=\"Bước 1 Sơ chế tôm Mì trứng trần\" src=\"https://cdn.tgdd.vn/2021/10/CookRecipe/GalleryStep/so-che-tom-7.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; display: block; overflow: hidden; border-radius: 4px; width: 340px; height: 340px; object-fit: cover; opacity: 1;\"></div><div class=\"img-method  twoitem\" style=\"margin: 0px 5px 0px 0px; padding: 0px; display: inline-block; overflow: hidden; vertical-align: top; cursor: pointer; border-radius: 4px; width: calc(50% - 5px); float: left;\"><img data-src=\"https://cdn.tgdd.vn/2021/10/CookRecipe/GalleryStep/so-che-tom-8.jpg\" data-picid=\"192443\" class=\"lazy\" alt=\"Bước 1 Sơ chế tôm Mì trứng trần\" src=\"https://cdn.tgdd.vn/2021/10/CookRecipe/GalleryStep/so-che-tom-8.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; display: block; overflow: hidden; border-radius: 4px; width: 340px; height: 340px; object-fit: cover; opacity: 1;\"></div></div></div></li><li style=\"margin: 0px; padding: 10px 24px; display: block; overflow: hidden;\"><label style=\"margin: 0px 16px 0px 0px; padding: 5px 12px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); outline: none; float: left; background: rgb(221, 221, 221); border-radius: 4px;\">2</label><div class=\"text-method\" style=\"margin: 0px; padding: 0px; overflow: hidden;\"><h3 style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 5px 0px 10px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 27.9px; color: rgb(51, 51, 51); outline: none; overflow: hidden;\">Nấu mì</h3><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; overflow: hidden; color: rgb(51, 51, 51);\">Bắc <a title=\"nồi\" href=\"https://www.dienmayxanh.com/noi\" target=\"_blank\" rel=\"noopener\" style=\"margin: 0px; padding: 0px; text-decoration: none; transition: all 0.2s ease 0s; color: rgb(40, 138, 214);\">nồi</a> lên <a title=\"bếp\" href=\"https://www.dienmayxanh.com/bep-dien\" target=\"_blank\" rel=\"noopener\" style=\"margin: 0px; padding: 0px; text-decoration: none; transition: all 0.2s ease 0s; color: rgb(40, 138, 214);\">bếp</a>, cho vào khoảng 200ml nước, đổ gói rau củ khô nằm trong gói mì vào. Thả mì vào luộc trong khoảng 3 phút rồi cho cho gói gia vị vào.</p><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; overflow: hidden; color: rgb(51, 51, 51);\">Dùng nĩa từ từ làm tơi phần mì này ra, cho 3 con tôm, cẩn thận đập 1 quả trứng gà và thêm 1 muỗng cà phê dầu ớt. Đậy nắp lai và nấu thêm 2 phút nữa thì tắt bếp.</p><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; overflow: hidden; color: rgb(51, 51, 51);\">Gắp mì tôm và topping cho vào <a title=\"chén\" href=\"https://www.dienmayxanh.com/chen-bat-chen-com\" target=\"_blank\" rel=\"noopener\" style=\"margin: 0px; padding: 0px; text-decoration: none; transition: all 0.2s ease 0s; color: rgb(40, 138, 214);\">chén</a> rồi rưới nước mì vào. Cẩn thận cho đặt trứng lên trên cùng, trộn đều và thưởng thức nhé!</p><div class=\"box-gallery\" style=\"margin: -10px 0px 0px; padding: 0px;\"><div class=\"img-method firstItem fouritem\" style=\"margin: 2.5px 5px 2.5px 0px; padding: 0px; display: inline-block; overflow: hidden; vertical-align: top; cursor: pointer; border-radius: 4px; width: calc(50% - 5px); float: left;\"><img data-src=\"https://cdn.tgdd.vn/2021/10/CookRecipe/GalleryStep/nau-mi.jpg\" data-picid=\"192444\" class=\"lazy\" alt=\"Bước 2 Nấu mì Mì trứng trần\" src=\"https://cdn.tgdd.vn/2021/10/CookRecipe/GalleryStep/nau-mi.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; display: block; overflow: hidden; border-radius: 4px; width: 340px; height: 340px; object-fit: cover; opacity: 1;\"></div><div class=\"img-method  fouritem\" style=\"margin: 2.5px 5px 2.5px 0px; padding: 0px; display: inline-block; overflow: hidden; vertical-align: top; cursor: pointer; border-radius: 4px; width: calc(50% - 5px); float: left;\"><img data-src=\"https://cdn.tgdd.vn/2021/10/CookRecipe/GalleryStep/nau-mi-1.jpg\" data-picid=\"192445\" class=\"lazy\" alt=\"Bước 2 Nấu mì Mì trứng trần\" src=\"https://cdn.tgdd.vn/2021/10/CookRecipe/GalleryStep/nau-mi-1.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; display: block; overflow: hidden; border-radius: 4px; width: 340px; height: 340px; object-fit: cover; opacity: 1;\"></div><div class=\"img-method  fouritem\" style=\"margin: 2.5px 5px 2.5px 0px; padding: 0px; display: inline-block; overflow: hidden; vertical-align: top; cursor: pointer; border-radius: 4px; width: calc(50% - 5px); float: left;\"><img data-src=\"https://cdn.tgdd.vn/2021/10/CookRecipe/GalleryStep/nau-mi-2.jpg\" data-picid=\"192446\" class=\"lazy\" alt=\"Bước 2 Nấu mì Mì trứng trần\" src=\"https://cdn.tgdd.vn/2021/10/CookRecipe/GalleryStep/nau-mi-2.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; display: block; overflow: hidden; border-radius: 4px; width: 340px; height: 340px; object-fit: cover; opacity: 1;\"></div><div class=\"img-method  fouritem\" style=\"margin: 2.5px 5px 2.5px 0px; padding: 0px; display: inline-block; overflow: hidden; vertical-align: top; cursor: pointer; border-radius: 4px; width: calc(50% - 5px); float: left;\"><img data-src=\"https://cdn.tgdd.vn/2021/10/CookRecipe/GalleryStep/nau-mi-3.jpg\" data-picid=\"192447\" class=\"lazy\" alt=\"Bước 2 Nấu mì Mì trứng trần\" src=\"https://cdn.tgdd.vn/2021/10/CookRecipe/GalleryStep/nau-mi-3.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; display: block; overflow: hidden; border-radius: 4px; width: 340px; height: 340px; object-fit: cover; opacity: 1;\"></div></div></div></li><li style=\"margin: 0px; padding: 10px 24px; display: block; overflow: hidden;\"><label style=\"margin: 0px 16px 0px 0px; padding: 5px 12px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 16px; line-height: 24px; color: rgb(51, 51, 51); outline: none; float: left; background: rgb(221, 221, 221); border-radius: 4px;\">3</label><div class=\"text-method\" style=\"margin: 0px; padding: 0px; overflow: hidden;\"><h3 style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 5px 0px 10px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 27.9px; color: rgb(51, 51, 51); outline: none; overflow: hidden;\">Thành phẩm</h3><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; overflow: hidden; color: rgb(51, 51, 51);\">Mì trứng trần với cách làm đơn giản, hấp dẫn với tôm tươi ngon giúp món mì thêm đầy đủ dinh dưỡng và bắt mắt hơn. Khi thưởng thức, bạn sẽ cảm nhận được trọn vẹn vị ngọt đậm đà, mặn mà cùng cay the the vô cùng hấp dẫn.</p><div class=\"box-gallery\" style=\"margin: -10px 0px 0px; padding: 0px;\"><div class=\"img-method firstItem oneitem\" style=\"margin: 0px; padding: 0px; display: inline-block; overflow: hidden; vertical-align: top; cursor: pointer; border-radius: 4px; width: calc(100% - 5px); min-width: 300px;\"><img data-src=\"https://cdn.tgdd.vn/2021/10/CookRecipe/GalleryStep/thanh-pham-2026.jpg\" data-picid=\"192448\" class=\"lazy\" alt=\"Bước 3 Thành phẩm Mì trứng trần\" src=\"https://cdn.tgdd.vn/2021/10/CookRecipe/GalleryStep/thanh-pham-2026.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; display: block; overflow: hidden; border-radius: 4px; width: 678.1px; height: auto; object-fit: cover; max-height: 450px; opacity: 1;\"></div></div></div></li></ul>', 'img/products/mytom1.jpg', '/img/products/mytom2.jpg', '/img/products/mytom3.jpg', 1, '2022-11-15 17:46:27'),
(7546, 23, 2, 1, 'Cơm điện', '<p><span style=\"color: rgb(51, 51, 51); font-family: Arial, Tahoma, sans-serif; font-size: 14px; text-align: justify;\">Nấu cơm bằng</span><a href=\"https://dienmaycholon.vn/noi-com-dien\" style=\"margin: 0px; padding: 0px; text-decoration: none; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(0, 84, 166); font-weight: 400; text-align: justify; background-color: rgb(255, 255, 255);\">&nbsp;nồi cơm điện</a><span style=\"color: rgb(51, 51, 51); font-family: Arial, Tahoma, sans-serif; font-size: 14px; text-align: justify;\">&nbsp;là một việc tưởng chừng như rất đơn giản, nhưng để nấu sao cho ngon và hạt cơm chín đều thì cũng cần có bí quyết đấy nhé!</span><br></p>', '<p style=\"padding: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(51, 51, 51); text-align: justify;\"><strong style=\"margin: 0px; padding: 0px;\">Bước 1: Đong gạo chính xác &nbsp;&nbsp;&nbsp;&nbsp;</strong></p><p align=\"center\" style=\"padding: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(51, 51, 51);\"><img class=\"lazy loaded\" alt=\"Đong gạo chính xác\" data-src=\"//cdn01.dienmaycholon.vn/filewebdmclnew/public//userupload/images/1(318).jpg\" src=\"https://cdn01.dienmaycholon.vn/filewebdmclnew/public//userupload/images/1(318).jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; height: auto; max-width: 100%; opacity: 1; transition: opacity 0.2s ease-in 0s;\"></p><p align=\"center\" style=\"padding: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(51, 51, 51);\"><em style=\"margin: 0px; padding: 0px;\">Đong gạo chính xác</em></p><p style=\"padding: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(51, 51, 51); text-align: justify;\">Hầu hết các loại nồi cơm điện đều có cốc đong gạo đi kèm để bạn có thể đong chính xác cho một lần nào. Với mỗi cốc có dung tích 150g gạo tương đương với 2 chén cơm. Hoặc bạn có thể dùng lon sữa để đong gạo chính xác cho một lần nấu.</p><p style=\"padding: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(51, 51, 51); text-align: justify;\"><strong style=\"margin: 0px; padding: 0px;\">Bước 2: Vo gạo&nbsp; sạch với nước</strong></p><p style=\"padding: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(51, 51, 51); text-align: justify;\">Một số loại gạo được nhà sản xuất khuyến cáo không cần vo vì trong gạo có một số dưỡng chất quan trọng như Vitamin, sắt… Vo gạo sẽ làm mất đi các dương chất. Nếu như không có ghi chú nào, bạn có thể vo gạo sạch với nước để loại bỏ thuốc trừ sâu, bụi trấu. lưu ý đừng vo sát quá để tránh làm nát gạo và mất đi chất dinh dưỡng.</p><p style=\"padding: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(51, 51, 51); text-align: justify;\"><strong style=\"margin: 0px; padding: 0px;\">Bước 3: Ngâm gạo trong 30 phút để cơm chín đều hơn</strong></p><p align=\"center\" style=\"padding: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(51, 51, 51);\"><img class=\"lazy loaded\" alt=\"Ngâm gạo 30 phút để cơm chín đều hơn\" data-src=\"//cdn01.dienmaycholon.vn/filewebdmclnew/public//userupload/images/3(237).jpg\" src=\"https://cdn01.dienmaycholon.vn/filewebdmclnew/public//userupload/images/3(237).jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; height: auto; max-width: 100%; opacity: 1; transition: opacity 0.2s ease-in 0s;\"></p><p align=\"center\" style=\"padding: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(51, 51, 51);\"><em style=\"margin: 0px; padding: 0px;\">Ngâm gạo trong 30 phút để cơm chín đều hơn</em></p><p style=\"padding: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(51, 51, 51); text-align: justify;\">Nếu như bạn có thời gian, bạn có thể ngâm gạo trong 30 phút để gạo nở đều hơn trong quá trình nấu. Việc này sẽ làm cho cơm ngon hơn, chín đều và không bị nát.</p><p style=\"padding: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(51, 51, 51); text-align: justify;\"><strong style=\"margin: 0px; padding: 0px;\">Bước 4: Đong nước phù hợp với lượng gạo</strong></p><p style=\"padding: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(51, 51, 51); text-align: justify;\">Nguyên tắc nấu cơ bản trong việc đóng nước để phù hợp với lượng gạo là: Số bát gạo = số bát nước thêm ½ chén.</p><p style=\"padding: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(51, 51, 51); text-align: justify;\"><strong style=\"margin: 0px; padding: 0px;\">Bước 5: Thêm một ít muối, bơ hoặc dầu ăn</strong></p><p align=\"center\" style=\"padding: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(51, 51, 51);\"><img class=\"lazy loaded\" alt=\"Thêm một ít muối, bơ hoặc dầu ăn\" data-src=\"//cdn01.dienmaycholon.vn/filewebdmclnew/public//userupload/images/5(161).jpg\" src=\"https://cdn01.dienmaycholon.vn/filewebdmclnew/public//userupload/images/5(161).jpg\" data-was-processed=\"true\" style=\"margin: 0px; padding: 0px; height: auto; max-width: 100%; opacity: 1; transition: opacity 0.2s ease-in 0s;\"></p><p align=\"center\" style=\"padding: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(51, 51, 51);\"><em style=\"margin: 0px; padding: 0px;\">Thêm một ít muối, bơ hoặc dầu ăn</em></p><p style=\"padding: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(51, 51, 51); text-align: justify;\">Việc này sẽ giúp cho cơm của bạn không bị ám màu, có vàng óng hạn chế việc cơm bị dính. Cơm thơm và có gia vị hơn.</p><p style=\"padding: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(51, 51, 51); text-align: justify;\"><strong style=\"margin: 0px; padding: 0px;\">Bước 6: Nấu cơm đúng quy trình</strong></p><p style=\"padding: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(51, 51, 51); text-align: justify;\">Sau khi đã vo gạo và cho thêm một tí gia vị, bạn lau bên ngoài lồng bằng miếng giẻ khô để đảm bảo bề mặt nồi khô ráo. Sau đó, đặt lòng nồi vào trong thân nồi, xoay nhẹ sao cho đáy nồi tiếp xúc trực tiếp với mâm điện. Đóng nắp lại, cắm điện và bật công tắc.</p><p style=\"padding: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(51, 51, 51); text-align: justify;\"><strong style=\"margin: 0px; padding: 0px;\">Bước 7: Ủ cơm trong vòng từ 5 đến 10 phút</strong></p><p style=\"padding: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 14px; line-height: 25px; color: rgb(51, 51, 51); text-align: justify;\">Sau khi nấu xong, bạn chuyển nồi cơm điện sang chế độ giữ ấm, lúc này bạn có thể rút điện ra và tuyệt đối không mở nắp trong vòng từ 5 đến 10 phút. Việc này sẽ giúp cơm khô bề mặt, chín đều và hạt cơm không bị dính vào thân nồi. Sau đó, mở nắp, xới đều cơm và thường thức một bữa ăn ngon. Chúc các bạn ngon miệng!</p>', 'img/products/com12.jpg', '/img/products/com13.jpg', '/img/products/com14.jpg', 1, '2022-11-18 18:46:18');
INSERT INTO `nguoidung_sanpham` (`MaSP`, `MaND`, `MaQuyen`, `MaDM`, `TenSP`, `MoTa`, `CachLam`, `HinhAnh1`, `HinhAnh2`, `HinhAnh3`, `TrangThai`, `ThoiGian`) VALUES
(7552, 32, 2, 3, 'Bánh chưng', '<p><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">“Cây nêu, tràng pháo, bánh chưng xanh” – có lẽ với </span><a title=\"ngày Tết\" href=\"https://www.dienmayxanh.com/vao-bep/ngay-le-tet\" target=\"_blank\" rel=\"noopener\" style=\"margin: 0px; padding: 0px; text-decoration: none; transition: all 0.2s ease 0s; color: rgb(74, 144, 226); font-family: Arial, Helvetica, sans-serif; font-size: 18px; font-weight: 400; background-color: rgb(255, 255, 255);\">ngày Tết</a><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"> cổ truyền của dân tộc, không nhà nào lại không có </span><a title=\"món bánh\" href=\"https://www.dienmayxanh.com/vao-bep/mon-banh\" target=\"_blank\" rel=\"noopener\" style=\"margin: 0px; padding: 0px; text-decoration: none; transition: all 0.2s ease 0s; color: rgb(74, 144, 226); font-family: Arial, Helvetica, sans-serif; font-size: 18px; font-weight: 400; background-color: rgb(255, 255, 255);\">món bánh</a><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"> chưng</span><br></p>', '<h3 id=\"qmenu1\" style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px 0px 10px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 20px; line-height: 31px; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); outline: none; overflow: hidden;\">1. Dùng nồi tôn (tole) để nấu bánh chưng xanh tự nhiên</h3><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">Nồi tole là loại nồi có khả năng tạo môi trường kiềm. Do vậy, thay vì dùng các loại nồi khác để nấu bánh chưng, bạn nên dùng nồi tole để nấu.</p><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">Vì môi trường kiềm sẽ giữ được màu xanh của lá dong, do đó giúp cho bánh chưng có một màu xanh thật tự nhiên.</p><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"><img class=\"lazy\" data-src=\"https://cdn.tgdd.vn/2020/08/content/26-760x403.jpg\" alt=\"Dùng nồi tôn\" width=\"760\" height=\"403\" src=\"https://cdn.tgdd.vn/2020/08/content/26-760x403.jpg\" style=\"margin: 5px 0px; padding: 0px; border: 0px; width: 740px; height: auto; display: inline; opacity: 1;\"></p><h3 id=\"qmenu2\" style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px 0px 10px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 20px; line-height: 31px; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); outline: none; overflow: hidden;\">2. Ngâm nếp qua nước tro</h3><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">Vì tro có tính kiềm nhẹ, nên trước khi nấu, một số gia đình miền Trung đã ngâm nếp cùng với nước tro. Điều này làm tăng độ kiềm của nếp, và từ đó khi nấu bánh chưng, nếp sẽ trong hơn, có màu xanh ngọc rất đẹp nhưng vẫn giữ được hương vị thơm ngon cần có của bánh chưng.</p><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"><img title=\"Dùng nước tro để ngâm nếp\" class=\"lazy\" data-src=\"https://cdn.tgdd.vn/Files/2016/01/04/767025/meo-luoc-banh-chung-xanh-tu-nhien-cho-ngay-tet-them-ven-tron-2.jpg\" alt=\"Dùng nước tro để ngâm nếp\" data-original=\"https://cdn.tgdd.vn/Files/2016/01/04/767025/meo-luoc-banh-chung-xanh-tu-nhien-cho-ngay-tet-them-ven-tron-2.jpg\" src=\"https://cdn.tgdd.vn/Files/2016/01/04/767025/meo-luoc-banh-chung-xanh-tu-nhien-cho-ngay-tet-them-ven-tron-2.jpg\" style=\"margin: 5px 0px; padding: 0px; border: 0px; width: 740px; height: auto; display: inline; opacity: 1;\"></p><h3 id=\"qmenu3\" style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px 0px 10px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 20px; line-height: 31px; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); outline: none; overflow: hidden;\">3. Dùng lá của củ riềng</h3><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">Bạn cũng có thể lấy lá riềng, xắt lát nhỏ rồi giã ra, chắt lấy nước lá riềng. Từ đó, lấy hỗn hợp này trộn với nếp đã được ngâm qua nước tro, sẽ giúp cho gạo nếp dẻo và thơm hơn. Hơn nữa, bánh chưng sẽ có màu xanh tuyệt vời từ ngoài vào trong.</p><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"><img class=\"lazy\" data-src=\"https://cdn.tgdd.vn/2020/08/content/27-760x1024.jpg\" alt=\"Dùng lá riềng\" width=\"760\" height=\"1024\" src=\"https://cdn.tgdd.vn/2020/08/content/27-760x1024.jpg\" style=\"margin: 5px 0px; padding: 0px; border: 0px; width: 740px; height: auto; display: inline; opacity: 1;\"></p><h3 id=\"qmenu4\" style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px 0px 10px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 20px; line-height: 31px; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); outline: none; overflow: hidden;\">4. Dùng nước chanh hoặc nước lá dứa</h3><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">Nước chanh tạo nên môi trường kiềm, nước lá dứa cũng vậy. Do đó mà bạn có thể ngâm nếp trong nước dứa khoảng 3 tiếng, còn với nước chanh – vì có độ kiềm mạnh hơn nên các bạn chỉ cần vắt chanh vào. Như vậy, bánh sẽ nhanh chín hơn và có được một màu xanh tự nhiên.</p><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"><img class=\"lazy\" data-src=\"https://cdn.tgdd.vn/2020/08/content/30-800x450.jpg\" alt=\"Dùng nước chanh hay nước lá dứa\" width=\"800\" height=\"450\" src=\"https://cdn.tgdd.vn/2020/08/content/30-800x450.jpg\" style=\"margin: 5px 0px; padding: 0px; border: 0px; width: 740px; height: auto; display: inline; opacity: 1;\"></p><h3 id=\"qmenu5\" style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px 0px 10px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 20px; line-height: 31px; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); outline: none; overflow: hidden;\">5. Dùng baking soda</h3><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">Khi nấu bánh chưng, nếu bạn cho vào một ít <a title=\"baking soda\" href=\"https://www.dienmayxanh.com/vao-bep/baking-soda-va-nhung-tac-dung-ki-dieu-00373\" target=\"_blank\" rel=\"noopener\" style=\"margin: 0px; padding: 0px; text-decoration: none; transition: all 0.2s ease 0s; color: rgb(40, 138, 214);\">baking soda</a> vào thì lá bánh sẽ được xanh và bánh cũng mau chín hơn nữa đấy. Baking soda có thể tìm mua ở các cửa hàng bán đồ làm bánh, siêu thị hay các kênh thương mại điện tử, đây không phải hóa chất độc hại nên bạn có thể yên tâm sử dụng nhé!</p><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"><img class=\"lazy\" data-src=\"https://cdn.tgdd.vn/2021/02/content/bk1-800x500.jpg\" alt=\" Dùng baking soda\" width=\"800\" height=\"500\" src=\"https://cdn.tgdd.vn/2021/02/content/bk1-800x500.jpg\" style=\"margin: 5px 0px; padding: 0px; border: 0px; width: 740px; height: auto; display: inline; opacity: 1;\"></p><h3 id=\"qmenu6\" style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px 0px 10px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 20px; line-height: 31px; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); outline: none; overflow: hidden;\">6. Chần lá gói bánh qua nước sôi</h3><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">Trước khi gói bánh bạn nên chần lá qua nước sôi để giúp cho lá trở nên mềm hơn, dễ gói hơn, đồng thời với cách này còn giúp diệt hết mầm nấm mốc trên lá, giúp lá xanh hơn.</p><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">Ngoài ra, lá dong mua về phải được rửa sạch từng lá một qua nhiều nước, xong dùng khăn sạch lau từng chiếc lá.<strong style=\"margin: 0px; padding: 0px;\"> </strong>Số lượng lá gói mỗi chiếc bánh cũng phải xem thời tiết, trời mát thì gồm 6 lá, còn trời nóng phải dùng 10 lá để phù hợp với việc bảo quản, giúp bánh giữ được lâu hơn.</p><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"><img class=\"lazy\" data-src=\"https://cdn.tgdd.vn/2021/02/content/bk2-800x500.jpg\" alt=\"Chần lá gói bánh qua nước sôi\" width=\"800\" height=\"500\" src=\"https://cdn.tgdd.vn/2021/02/content/bk2-800x500.jpg\" style=\"margin: 5px 0px; padding: 0px; border: 0px; width: 740px; height: auto; display: inline; opacity: 1;\"></p><h3 id=\"qmenu7\" style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px 0px 10px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 20px; line-height: 31px; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); outline: none; overflow: hidden;\">7. Rửa thật sạch nếp trước khi gói bánh</h3><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">Rửa sạch nếp trước khi gói bánh cũng là cách giúp cho bánh có màu đẹp hơn, thơm ngon hơn và giữ được lâu hơn. Nếp phải được đãi thật sạch qua hàng chục nước đến khi nào nước trong mới thôi. Làm như vậy sẽ giúp rửa trôi hết bụi cám bám quanh hạt nếp đi, bánh sẽ có màu trong xanh rất đẹp.</p><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"><img class=\"lazy\" data-src=\"https://cdn.tgdd.vn/2021/02/content/bk3-800x500.jpg\" alt=\" Rửa thật sạch nếp trước khi gói bánh\" width=\"800\" height=\"500\" src=\"https://cdn.tgdd.vn/2021/02/content/bk3-800x500.jpg\" style=\"margin: 5px 0px; padding: 0px; border: 0px; width: 740px; height: auto; display: inline; opacity: 1;\"></p><h3 id=\"qmenu8\" style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px 0px 10px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 20px; line-height: 31px; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); outline: none; overflow: hidden;\">8. Dùng lá dư chèn dưới đáy nồi</h3><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">Khi nấu bánh, dùng lá dư chèn dưới đáy nồi và xung quanh mặt trong của nồi là cách vừa bảo vệ bánh, giúp bánh không bị cháy, vừa giúp nước nấu bánh trở nên xanh hơn.</p><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"><img class=\"lazy\" data-src=\"https://cdn.tgdd.vn/2021/02/content/bk4-800x500.jpg\" alt=\"Dùng lá dư chèn dưới đáy nồi\" width=\"800\" height=\"500\" src=\"https://cdn.tgdd.vn/2021/02/content/bk4-800x500.jpg\" style=\"margin: 5px 0px; padding: 0px; border: 0px; width: 740px; height: auto; display: inline; opacity: 1;\"></p><h3 id=\"qmenu9\" style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px 0px 10px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 20px; line-height: 31px; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); outline: none; overflow: hidden;\">9. Rửa bánh qua nước lạnh</h3><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">Khi nấu bánh được một nửa thời gian thì các bạn nên thay toàn bộ nước luộc bánh chưng, cho vào nước luộc mới, như vậy sẽ giúp bánh được ngon hơn và có màu xanh tự nhiên.</p><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\"><img class=\"lazy\" data-src=\"https://laodongthudo.vn/stores/news_dataimages/thamle/012020/23/23/5845_82505236_586820038717002_1235681469951639552_n.jpg\" alt=\"Rửa bánh qua nước lạnh\" width=\"800\" height=\"533\" src=\"https://laodongthudo.vn/stores/news_dataimages/thamle/012020/23/23/5845_82505236_586820038717002_1235681469951639552_n.jpg\" style=\"margin: 5px 0px; padding: 0px; border: 0px; width: 740px; height: auto; display: inline; opacity: 1;\"></p><h3 id=\"qmenu10\" style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px 0px 10px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 20px; line-height: 31px; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); outline: none; overflow: hidden;\">10. Dùng vật nặng đè lên bánh chưng khi nấu xong</h3><p style=\"margin-bottom: 20px; padding: 0px; margin-block: 0px; text-rendering: geometricprecision; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 18px;\">Bánh sau khi luộc xong các bạn để ra bàn rồi dùng mặt thớt hay tấm ván nặng đè lên để ép ra nước. Với cách này sẽ giúp cho bánh được chắc chăn hơn và để được lâu hơn sơ với bình thường.</p>', 'img/products/banhchung1.jpg', '/img/products/banhchung2.jpg', '/img/products/banhchung3.jpg', 1, '2022-11-20 19:14:56');

